import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import axios from 'axios'
import { toast } from 'sonner'

const API_BASE_URL = import.meta.env.VITE_APP_BACKEND_URL || 'http://localhost:8000'

interface User {
  id: number
