            errorMessage = error.response.data.detail
          } else if (error.message) {
            errorMessage = error.message
          }

          set({ error: errorMessage, loading: false })
          toast.error(errorMessage)
        }
        return false
      },

      setCurrentTask: (task) => {
        set({ currentTask: task })
      },

      // Comment actions
