@router.get("/dashboard/")
def crm_dashboard(db: Session = Depends(get_db), user: User = Depends(get_current_user)):
    try:
        # Totals by deal stage
        deal_stages = db.query(Deal.stage).distinct().all()
        deal_totals = {}
        for (stage,) in deal_stages:
            count = db.query(Deal).filter(Deal.stage == stage, Deal.is_deleted == False).count()
            value_sum = db.query(Deal).filter(Deal.stage == stage, Deal.is_deleted == False).with_entities(func.sum(Deal.value)).scalar() or 0
            deal_totals[stage] = {"count": count, "total_value": value_sum}
        # Totals by contact status
        contact_statuses = db.query(Contact.status).distinct().all()
        contact_totals = {}
        for (status,) in contact_statuses:
            count = db.query(Contact).filter(Contact.status == status, Contact.is_deleted == False).count()
            contact_totals[status] = {"count": count}
        return {
            "deal_totals_by_stage": deal_totals,
            "contact_totals_by_status": contact_totals
        }
    except Exception as e:
        from fastapi import Response
        return Response(content=f"Dashboard error: {str(e)}", media_type="text/plain", status_code=500)
