from fastapi import APIRouter, HTTPException, status
from typing import List
from models.todo import Todo, TodoCreate, TodoUpdate
from datetime import datetime
import threading

router = APIRouter(prefix="/todos", tags=["todos"])

# Simple in-memory store protected by a lock
_todos: List[Todo] = []
_next_id = 1
_lock = threading.Lock()


@router.get("/", response_model=List[Todo])
def list_todos():
    return _todos


@router.post("/", response_model=Todo, status_code=status.HTTP_201_CREATED)
def create_todo(payload: TodoCreate):
    global _next_id
    with _lock:
        todo = Todo(id=_next_id, title=payload.title, description=payload.description, completed=payload.completed, created_at=datetime.utcnow())
        _todos.append(todo)
        _next_id += 1
    return todo


@router.get("/{todo_id}/", response_model=Todo)
def get_todo(todo_id: int):
    for t in _todos:
        if t.id == todo_id:
            return t
    raise HTTPException(status_code=404, detail="Todo not found")


@router.put("/{todo_id}/", response_model=Todo)
def update_todo(todo_id: int, payload: TodoUpdate):
    for idx, t in enumerate(_todos):
        if t.id == todo_id:
            updated = t.copy(update=payload.model_dump(exclude_unset=True))
            _todos[idx] = updated
            return updated
    raise HTTPException(status_code=404, detail="Todo not found")


@router.delete("/{todo_id}/", status_code=status.HTTP_204_NO_CONTENT)
def delete_todo(todo_id: int):
    for idx, t in enumerate(_todos):
        if t.id == todo_id:
            del _todos[idx]
            return
    raise HTTPException(status_code=404, detail="Todo not found")
