```xml
<action type="file" filePath="backend/services/auth_service.py">
from fastapi import APIRouter, HTTPException, Depends
from fastapi.security import OAuth2PasswordRequestForm
from pydantic import BaseModel
from datetime import datetime, timedelta
import hashlib
import uuid
from models.user import UserInDB, UserCreate, UserRead

router = APIRouter(prefix="/auth", tags=["Authentication"])

# In-memory storage for demo (replace with database later)
users_db = {}
tokens_db = {}

class Token(BaseModel):
    access_token: str
    token_type: str

def hash_password(password: str) -> str:
    """Simple password hashing for demo (use proper hashing in production)"""
    return hashlib.sha256(f"mvp_salt_{password}".encode()).hexdigest()

@router.post("/signup", response_model=UserRead, status_code=201)
async def signup(user: UserCreate):
    # Check if user already exists
    if user.email in [u.email for u in users_db.values()]:
        raise HTTPException(status_code=400, detail="Email already registered")

    # Create user in DB
    user_id = str(uuid.uuid4())
    hashed_pw = hash_password(user.password)
    db_user = UserInDB(
        id=user_id,
        email=user.email,
        full_name=user.full_name,
        role=user.role,
        hashed_password=hashed_pw
    )
    users_db[user_id] = db_user

    # Return user without password
    return UserRead(**db_user.dict())

@router.post("/login", response_model=Token)
async def login(form_data: OAuth2PasswordRequestForm = Depends()):
    # Find user by email
    user = next((u for u in users_db.values() if u.email == form_data.username), None)

    # Verify user and password
    if not user or hash_password(form_data.password) != user.hashed_password:
        raise HTTPException(status_code=401, detail="Invalid credentials")

    # Create token (use JWT in production)
    token = str(uuid.uuid4())
    tokens_db[token] = {
        "user_id": user.id,
        "expires": datetime.utcnow() + timedelta(hours=24)
    }

    return {"access_token": token, "token_type": "bearer"}

@router.post("/reset-password")
async def reset_password(email: str):
    # In a real app, send password reset email
    return {"message": "Password reset instructions sent to your email"}

@router.get("/me", response_model=UserRead)
async def get_current_user(token: str):
    token_data = tokens_db.get(token)
    if not token_data or token_data["expires"] < datetime.utcnow():
        raise HTTPException(status_code=401, detail="Invalid token")

    user = users_db.get(token_data["user_id"])
    if not user:
        raise HTTPException(status_code=404, detail="User not found")

    return UserRead(**user.dict())
</action>

