<action type="file" filePath="frontend/src/services/authService.ts">
const API_BASE = import.meta.env.VITE_API_URL;

// Simple token management
let authToken: string | null = null;

export const authService = {
  // Store token in memory and localStorage
  setToken(token: string) {
    authToken = token;
    localStorage.setItem('authToken', token);
  },

  // Get token from memory or localStorage
  getToken() {
    if (!authToken) {
      authToken = localStorage.getItem('authToken');
    }
    return authToken;
  },

  // Clear token
  clearToken() {
    authToken = null;
    localStorage.removeItem('authToken');
  },

  async signup(userData: { 
    email: string; 
    full_name: string; 
    password: string; 
    role: string 
  }) {
    const response = await fetch(`${API_BASE}/auth/signup/`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData),
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.detail || 'Signup failed');
    }
    return response.json();
  },

  async login(credentials: { username: string; password: string }) {
    const formData = new URLSearchParams();
    formData.append('username', credentials.username);
    formData.append('password', credentials.password);

    const response = await fetch(`${API_BASE}/auth/login/`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: 'username=' + encodeURIComponent(credentials.username) + 
             '&password=' + encodeURIComponent(credentials.password),
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.detail || 'Login failed');
    }

    const data = await response.json();
    this.setToken(data.access_token);
    return data;
  },

  async getCurrentUser() {
    const token = this.getToken();
    if (!token) throw new Error('Not authenticated');

    const response = await fetch(`${API_BASE}/auth/me/`, {
      method: 'GET',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    });

    if (!response.ok) {
      if (response.status === 401) this.clearToken();
      throw new Error('Failed to fetch user');
    }
    return response.json();
  },

  async resetPassword(email: string) {
    const response = await fetch(`${API_BASE}/auth/reset-password/`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email }),
    });

    if (!response.ok) {
      throw new Error('Failed to send reset instructions');
    }
    return response.json();
  }
};

// Helper function to encode form data
function encodeURIComponent(str: string): string {
  return encodeURIComponent(str).replace(/%20/g, '+');
}
</action>
