import { useLocation } from 'react-router-dom'
import { Home, ListTodo } from "lucide-react"

import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from "@/components/ui/sidebar"

// Route groups for organized sidebar
const routeGroups = [
  {
    title: "Overview",
    items: [
      { title: "Home", url: "/", icon: Home },
    ]
  },
  {
    title: "Tasks",
    items: [
      { title: "Todos", url: "/todos", icon: ListTodo },
    ]
  },
]

export function AppSidebar() {
  const location = useLocation()

  const isActiveRoute = (url: string) => {
    if (url === "/") return location.pathname === "/"
    if (url.includes(":")) {
      // Handle parameterized routes like /users/:id
      const baseRoute = url.split("/:")[0]
      return location.pathname.startsWith(baseRoute)
    }
    return location.pathname === url || location.pathname.startsWith(url + "/")
  }

  return (
    <Sidebar className="border-r">
      <SidebarContent>
        {routeGroups.map((group) => (
          <SidebarGroup key={group.title} className="py-0">
            <SidebarGroupLabel className="text-xs font-semibold text-muted-foreground uppercase tracking-wider py-2">
              {group.title}
            </SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {group.items.map((item) => (
                  <SidebarMenuItem key={item.title} className="py-0.5">
                    <SidebarMenuButton 
                      asChild
                      isActive={isActiveRoute(item.url)}
                      className="py-1.5"
                    >
                      <a href={item.url}>
                        <item.icon className="h-4 w-4" />
                        <span className="text-sm">{item.title}</span>
                      </a>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>
    </Sidebar>
  )
}
