import React, { useEffect, useState } from 'react';
import {
  Box,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  TableContainer,
  Spinner,
  Alert,
  AlertIcon,
  Text,
  Badge,
  Flex,
  Button,
  Input,
  InputGroup,
  InputLeftElement,
  Stack,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  FormControl,
  FormLabel,
  FormErrorMessage,
  useToast,
  Select,
  HStack,
  Wrap,
  WrapItem,
  Tag as ChakraTag,
  TagLabel,
  TagCloseButton,
  IconButton,
} from '@chakra-ui/react';
import { SearchIcon, AddIcon, EditIcon, DeleteIcon } from '@chakra-ui/icons';
import { useForm } from 'react-hook-form';

// Define types for Contact and Tag
interface Tag {
  id: number;
  name: string;
}

interface Contact {
  id: number;
  email: string;
  first_name?: string;
  last_name?: string;
  created_at: string;
  tags: Tag[];
}

interface ContactFormInputs {
  email: string;
  first_name?: string;
  last_name?: string;
}

const API_URL = import.meta.env.VITE_API_URL;

const ContactList: React.FC = () => {
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [selectedContact, setSelectedContact] = useState<Contact | null>(null);
  const [selectedTagId, setSelectedTagId] = useState<number | null>(null);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const toast = useToast();

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors, isSubmitting },
  } = useForm<ContactFormInputs>();

  const fetchContacts = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_URL}/contacts/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data: Contact[] = await response.json();
      setContacts(data);
    } catch (e: any) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchTags = async () => {
    try {
      const response = await fetch(`${API_URL}/tags/`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data: Tag[] = await response.json();
      setTags(data);
    } catch (e: any) {
      console.error('Error fetching tags:', e);
    }
  };

  useEffect(() => {
    fetchContacts();
    fetchTags();
  }, []);

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
  };

  const filteredContacts = contacts.filter(contact =>
    contact.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.first_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.last_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.tags.some(tag => tag.name.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handleAddContact = () => {
    setSelectedContact(null);
    reset({ email: '', first_name: '', last_name: '' });
    onOpen();
  };

  const handleEditContact = (contact: Contact) => {
    setSelectedContact(contact);
    reset({
      email: contact.email,
      first_name: contact.first_name,
      last_name: contact.last_name,
    });
    onOpen();
  };

  const handleDeleteContact = async (contactId: number) => {
    if (!window.confirm('Are you sure you want to delete this contact?')) {
      return;
    }
    try {
      const response = await fetch(`${API_URL}/contacts/${contactId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      toast({
        title: 'Contact deleted.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
      fetchContacts(); // Refresh the list
    } catch (e: any) {
      toast({
        title: 'Error deleting contact.',
        description: e.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const onSubmit = async (data: ContactFormInputs) => {
    try {
      let response;
      if (selectedContact) {
        // Update existing contact
        response = await fetch(`${API_URL}/contacts/${selectedContact.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data),
        });
      } else {
        // Create new contact
        response = await fetch(`${API_URL}/contacts/`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data),
        });
      }

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
      }

      toast({
        title: selectedContact ? 'Contact updated.' : 'Contact created.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
      onClose();
      fetchContacts(); // Refresh the list
    } catch (e: any) {
      toast({
        title: selectedContact ? 'Error updating contact.' : 'Error creating contact.',
        description: e.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const handleAddTagToContact = async (contactId: number, tagId: number) => {
    try {
      const response = await fetch(`${API_URL}/contacts/${contactId}/tags/${tagId}`, {
        method: 'POST',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const updatedContact: Contact = await response.json();
      setContacts(contacts.map(contact => 
        contact.id === contactId ? updatedContact : contact
      ));

      toast({
        title: 'Tag added to contact.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (e: any) {
      toast({
        title: 'Error adding tag to contact.',
        description: e.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const handleRemoveTagFromContact = async (contactId: number, tagId: number) => {
    try {
      const response = await fetch(`${API_URL}/contacts/${contactId}/tags/${tagId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const updatedContact: Contact = await response.json();
      setContacts(contacts.map(contact => 
        contact.id === contactId ? updatedContact : contact
      ));

      toast({
        title: 'Tag removed from contact.',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (e: any) {
      toast({
        title: 'Error removing tag from contact.',
        description: e.message,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  if (loading) {
    return (
      <Flex justify="center" align="center" height="200px">
        <Spinner size="xl" />
      </Flex>
    );
  }

  if (error) {
    return (
      <Alert status="error">
        <AlertIcon />
        Error loading contacts: {error}
      </Alert>
    );
  }

  return (
    <Box>
      <Flex mb={4} justify="space-between" align="center">
        <InputGroup maxW="300px">
          <InputLeftElement pointerEvents="none">
            <SearchIcon color="gray.300" />
          </InputLeftElement>
          <Input
            type="text"
            placeholder="Search contacts..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </InputGroup>
        <Button leftIcon={<AddIcon />} colorScheme="teal" onClick={handleAddContact}>
          Add Contact
        </Button>
      </Flex>

      <TableContainer>
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Email</Th>
              <Th>First Name</Th>
              <Th>Last Name</Th>
              <Th>Tags</Th>
              <Th>Created At</Th>
              <Th>Actions</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredContacts.length === 0 ? (
              <Tr>
                <Td colSpan={6} textAlign="center">No contacts found.</Td>
              </Tr>
            ) : (
              filteredContacts.map(contact => (
                <Tr key={contact.id}>
                  <Td>{contact.email}</Td>
                  <Td>{contact.first_name || '-'}</Td>
                  <Td>{contact.last_name || '-'}</Td>
                  <Td>
                    <Stack spacing={2}>
                      <Wrap>
                        {contact.tags.map(tag => (
                          <WrapItem key={tag.id}>
                            <ChakraTag colorScheme="purple" size="sm">
                              <TagLabel>{tag.name}</TagLabel>
                              <TagCloseButton 
                                onClick={() => handleRemoveTagFromContact(contact.id, tag.id)} 
                              />
                            </ChakraTag>
                          </WrapItem>
                        ))}
                      </Wrap>
                      <HStack mt={2}>
                        <Select 
                          size="sm" 
                          placeholder="Select tag to add"
                          value={selectedTagId || ''}
                          onChange={(e) => setSelectedTagId(Number(e.target.value))}
                        >
                          {tags
                            .filter(tag => !contact.tags.some(contactTag => contactTag.id === tag.id))
                            .map(tag => (
                              <option key={tag.id} value={tag.id}>
                                {tag.name}
                              </option>
                            ))}
                        </Select>
                        <Button 
                          size="sm" 
                          colorScheme="teal"
                          onClick={() => {
                            if (selectedTagId) {
                              handleAddTagToContact(contact.id, selectedTagId);
                              setSelectedTagId(null);
                            }
                          }}
                          isDisabled={!selectedTagId}
                        >
                          Add Tag
                        </Button>
                      </HStack>
                    </Stack>
                  </Td>
                  <Td>{new Date(contact.created_at).toLocaleDateString()}</Td>
                  <Td>
                    <Flex>
                      <Button size="sm" leftIcon={<EditIcon />} onClick={() => handleEditContact(contact)} mr={2}>
                        Edit
                      </Button>
                      <Button size="sm" leftIcon={<DeleteIcon />} colorScheme="red" onClick={() => handleDeleteContact(contact.id)}>
                        Delete
                      </Button>
                    </Flex>
                  </Td>
                </Tr>
              ))
            )}
          </Tbody>
        </Table>
      </TableContainer>

      {/* Add/Edit Contact Modal */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>{selectedContact ? 'Edit Contact' : 'Add New Contact'}</ModalHeader>
          <ModalCloseButton />
          <form onSubmit={handleSubmit(onSubmit)}>
            <ModalBody pb={6}>
              <FormControl isInvalid={!!errors.email} isRequired>
                <FormLabel>Email</FormLabel>
                <Input
                  {...register('email', {
                    required: 'Email is required',
                    pattern: {
                      value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                      message: 'Invalid email address',
                    },
                  })}
                  placeholder="Email"
                  type="email"
                />
                <FormErrorMessage>{errors.email && errors.email.message}</FormErrorMessage>
              </FormControl>

              <FormControl mt={4}>
                <FormLabel>First Name</FormLabel>
                <Input {...register('first_name')} placeholder="First Name" />
              </FormControl>

              <FormControl mt={4}>
                <FormLabel>Last Name</FormLabel>
                <Input {...register('last_name')} placeholder="Last Name" />
              </FormControl>
            </ModalBody>

            <ModalFooter>
              <Button colorScheme="blue" mr={3} type="submit" isLoading={isSubmitting}>
                {selectedContact ? 'Update' : 'Create'}
              </Button>
              <Button onClick={onClose}>Cancel</Button>
            </ModalFooter>
          </form>
        </ModalContent>
      </Modal>
    </Box>
  );
};

export default ContactList;
</action>

