export enum PropertyType {
  APARTMENT = 'apartment',
  HOUSE = 'house',
  CONDO = 'condo',
  VILLA = 'villa',
  CABIN = 'cabin',
  TOWNHOUSE = 'townhouse',
}

export enum Amenity {
  WIFI = 'wifi',
  KITCHEN = 'kitchen',
  PARKING = 'parking',
  POOL = 'pool',
  GYM = 'gym',
  AIR_CONDITIONING = 'air_conditioning',
  HEATING = 'heating',
  WASHER = 'washer',
  DRYER = 'dryer',
  TV = 'tv',
  WORKSPACE = 'workspace',
  PETS_ALLOWED = 'pets_allowed',
  SMOKING_ALLOWED = 'smoking_allowed',
}

export interface PropertyImage {
  url: string;
  alt_text?: string;
  is_primary?: boolean;
}

export interface PropertyLocation {
  address: string;
  city: string;
  state: string;
  country: string;
  zip_code: string;
  latitude: number;
  longitude: number;
}

export interface PropertyPricing {
  base_price: number;
  currency: string;
  cleaning_fee?: number;
  service_fee?: number;
  taxes?: number;
  weekly_discount?: number;
  monthly_discount?: number;
}

export interface PropertyAvailability {
  start_date: string;
  end_date: string;
  minimum_stay: number;
  maximum_stay?: number;
}

export interface PropertyHost {
  id: string;
  name: string;
  avatar_url?: string;
  is_superhost: boolean;
  joined_date: string;
  response_rate?: number;
  response_time?: string;
}

export interface Property {
  id: string;
  title: string;
  description: string;
  property_type: PropertyType;
  property_subtype?: string;
  max_guests: number;
  bedrooms: number;
  bathrooms: number;
  beds: number;
  square_feet?: number;
  location: PropertyLocation;
  pricing: PropertyPricing;
  images: PropertyImage[];
  amenities: Amenity[];
  host: PropertyHost;
  rating?: number;
  review_count: number;
  created_at: string;
  updated_at: string;
  is_active: boolean;
}

export interface PropertySummary {
  id: string;
  title: string;
  property_type: PropertyType;
  max_guests: number;
  bedrooms: number;
  bathrooms: number;
  location: PropertyLocation;
  pricing: PropertyPricing;
  primary_image?: PropertyImage;
  rating?: number;
  review_count: number;
  is_superhost: boolean;
}
</action>
