import { useState } from 'react';
import { ChevronLeft, ChevronRight, Heart } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';

interface PropertyImageProps {
  images: string[];
  alt: string;
  className?: string;
}

export default function PropertyImage({ images, alt, className }: PropertyImageProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLiked, setIsLiked] = useState(false);

  const nextImage = (e: React.MouseEvent) => {
    e.stopPropagation();
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  const prevImage = (e: React.MouseEvent) => {
    e.stopPropagation();
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
  };

  const toggleLike = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsLiked(!isLiked);
  };

  if (!images || images.length === 0) {
    return (
      <div className={cn("relative aspect-square bg-gray-200 rounded-lg", className)}>
        <div className="absolute inset-0 flex items-center justify-center text-gray-400">
          No Image Available
        </div>
      </div>
    );
  }

  return (
    <div className={cn("relative aspect-square overflow-hidden rounded-lg group", className)}>
      <img
        src={images[currentIndex]}
        alt={alt}
        className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
      />

      {/* Navigation arrows */}
      {images.length > 1 && (
        <>
          <Button
            variant="ghost"
            size="icon"
            className="absolute left-2 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white opacity-0 group-hover:opacity-100 transition-opacity"
            onClick={prevImage}
          >
            <ChevronLeft className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            className="absolute right-2 top-1/2 -translate-y-1/2 bg-white/80 hover:bg-white opacity-0 group-hover:opacity-100 transition-opacity"
            onClick={nextImage}
          >
            <ChevronRight className="h-4 w-4" />
          </Button>
        </>
      )}

      {/* Image indicators */}
      {images.length > 1 && (
        <div className="absolute bottom-2 left-1/2 -translate-x-1/2 flex gap-1">
          {images.map((_, index) => (
            <button
              key={index}
              className={cn(
                "w-1.5 h-1.5 rounded-full transition-all",
                index === currentIndex ? "bg-white w-4" : "bg-white/50"
              )}
              onClick={(e) => {
                e.stopPropagation();
                setCurrentIndex(index);
              }}
            />
          ))}
        </div>
      )}

      {/* Like button */}
      <Button
        variant="ghost"
        size="icon"
        className="absolute top-3 right-3 bg-white/80 hover:bg-white"
        onClick={toggleLike}
      >
        <Heart
          className={cn(
            "h-4 w-4 transition-colors",
            isLiked ? "fill-red-500 text-red-500" : "text-gray-700"
          )}
        />
      </Button>
    </div>
  );
}
