import { Star } from 'lucide-react';
import PropertyImage from './PropertyImage';
import { Property } from '@/types/property';

interface PropertyCardProps {
  property: Property;
  onClick?: () => void;
}

export default function PropertyCard({ property, onClick }: PropertyCardProps) {
  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
    }).format(price);
  };

  const calculateAverageRating = () => {
    if (!property.reviews || property.reviews.length === 0) return null;

    const sum = property.reviews.reduce((acc, review) => acc + review.rating, 0);
    return (sum / property.reviews.length).toFixed(2);
  };

  const rating = calculateAverageRating();

  return (
    <div 
      className="group cursor-pointer"
      onClick={onClick}
    >
      <PropertyImage 
        images={property.images || []} 
        alt={property.title}
        className="mb-3"
      />

      <div className="space-y-1">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className="font-medium text-gray-900 line-clamp-1">
              {property.location.city}, {property.location.country}
            </h3>
            <p className="text-sm text-gray-600 line-clamp-1">
              {property.title}
            </p>
            <p className="text-sm text-gray-600">
              {property.type} ‚Ä¢ {property.maxGuests} guests
            </p>
          </div>

          {rating && (
            <div className="flex items-center gap-1 ml-2">
              <Star className="h-3.5 w-3.5 fill-current" />
              <span className="text-sm font-medium">{rating}</span>
            </div>
          )}
        </div>

        <p className="text-sm">
          <span className="font-semibold">{formatPrice(property.pricePerNight)}</span>
          <span className="text-gray-600"> night</span>
        </p>
      </div>
    </div>
  );
}
