import { useState, useEffect } from 'react';
import { Property } from '@/types/property';
import PropertyCard from './PropertyCard';
import { Skeleton } from '@/components/ui/skeleton';
import { searchService } from '@/services/searchService';

interface PropertyGridProps {
  properties?: Property[];
  loading?: boolean;
  onPropertyClick?: (property: Property) => void;
}

export default function PropertyGrid({ properties: initialProperties, loading: initialLoading, onPropertyClick }: PropertyGridProps) {
  const [properties, setProperties] = useState<Property[]>(initialProperties || []);
  const [loading, setLoading] = useState(initialLoading || false);

  useEffect(() => {
    if (initialProperties !== undefined) {
      setProperties(initialProperties);
    }
  }, [initialProperties]);

  useEffect(() => {
    if (initialLoading !== undefined) {
      setLoading(initialLoading);
    }
  }, [initialLoading]);

  useEffect(() => {
    if (!initialProperties) {
      loadProperties();
    }
  }, []);

  const loadProperties = async () => {
    setLoading(true);
    try {
      const response = await searchService.searchProperties({});
      setProperties(response.properties);
    } catch (error) {
      console.error('Failed to load properties:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {Array.from({ length: 8 }).map((_, index) => (
          <div key={index} className="space-y-3">
            <Skeleton className="aspect-square rounded-lg" />
            <div className="space-y-2">
              <Skeleton className="h-4 w-3/4" />
              <Skeleton className="h-4 w-1/2" />
              <Skeleton className="h-4 w-1/4" />
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (!properties || properties.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-500">No properties found</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {properties.map((property) => (
        <PropertyCard
          key={property.id}
          property={property}
          onClick={() => onPropertyClick?.(property)}
        />
      ))}
    </div>
  );
}
</action>
