import React, { useState } from 'react';
import { Search, MapPin, Calendar, Users } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Calendar as CalendarComponent } from '@/components/ui/calendar';
import { Input } from '@/components/ui/input';
import { cn } from '@/lib/utils';

interface SearchBarProps {
  onSearch: (searchParams: {
    location: string;
    checkIn: Date | undefined;
    checkOut: Date | undefined;
    guests: number;
  }) => void;
}

export function SearchBar({ onSearch }: SearchBarProps) {
  const [location, setLocation] = useState('');
  const [checkIn, setCheckIn] = useState<Date | undefined>();
  const [checkOut, setCheckOut] = useState<Date | undefined>();
  const [guests, setGuests] = useState(1);
  const [openPopover, setOpenPopover] = useState<'checkIn' | 'checkOut' | 'guests' | null>(null);

  const handleSearch = () => {
    onSearch({ location, checkIn, checkOut, guests });
  };

  return (
    <div className="w-full max-w-4xl mx-auto">
      <div className="bg-white rounded-full shadow-lg border border-gray-200 p-2">
        <div className="flex items-center">
          {/* Location Input */}
          <div className="flex-1 px-6 py-3 border-r border-gray-200">
            <div className="flex items-center gap-2">
              <MapPin className="h-4 w-4 text-gray-500" />
              <Input
                type="text"
                placeholder="Where are you going?"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="border-none p-0 focus:ring-0 text-sm placeholder:text-gray-500"
              />
            </div>
          </div>

          {/* Check-in Date */}
          <Popover open={openPopover === 'checkIn'} onOpenChange={(open) => setOpenPopover(open ? 'checkIn' : null)}>
            <PopoverTrigger asChild>
              <button className="flex-1 px-6 py-3 border-r border-gray-200 text-left">
                <div className="text-xs font-semibold text-gray-900">CHECK-IN</div>
                <div className="text-sm text-gray-500">
                  {checkIn ? checkIn.toLocaleDateString() : 'Add dates'}
                </div>
              </button>
            </PopoverTrigger>
            <PopoverContent className="w-auto p-0" align="start">
              <CalendarComponent
                mode="single"
                selected={checkIn}
                onSelect={(date) => {
                  setCheckIn(date);
                  setOpenPopover(null);
                }}
                disabled={(date) => date < new Date()}
              />
            </PopoverContent>
          </Popover>

          {/* Check-out Date */}
          <Popover open={openPopover === 'checkOut'} onOpenChange={(open) => setOpenPopover(open ? 'checkOut' : null)}>
            <PopoverTrigger asChild>
              <button className="flex-1 px-6 py-3 border-r border-gray-200 text-left">
                <div className="text-xs font-semibold text-gray-900">CHECK-OUT</div>
                <div className="text-sm text-gray-500">
                  {checkOut ? checkOut.toLocaleDateString() : 'Add dates'}
                </div>
              </button>
            </PopoverTrigger>
            <PopoverContent className="w-auto p-0" align="start">
              <CalendarComponent
                mode="single"
                selected={checkOut}
                onSelect={(date) => {
                  setCheckOut(date);
                  setOpenPopover(null);
                }}
                disabled={(date) => date <= (checkIn || new Date())}
              />
            </PopoverContent>
          </Popover>

          {/* Guests */}
          <Popover open={openPopover === 'guests'} onOpenChange={(open) => setOpenPopover(open ? 'guests' : null)}>
            <PopoverTrigger asChild>
              <button className="flex-1 px-6 py-3 text-left">
                <div className="text-xs font-semibold text-gray-900">GUESTS</div>
                <div className="text-sm text-gray-500">
                  {guests} {guests === 1 ? 'guest' : 'guests'}
                </div>
              </button>
            </PopoverTrigger>
            <PopoverContent className="w-80" align="end">
              <div className="p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <div className="font-semibold">Guests</div>
                    <div className="text-sm text-gray-500">How many guests?</div>
                  </div>
                  <div className="flex items-center gap-3">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => setGuests(Math.max(1, guests - 1))}
                    >
                      -
                    </Button>
                    <span className="w-8 text-center">{guests}</span>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => setGuests(guests + 1)}
                    >
                      +
                    </Button>
                  </div>
                </div>
              </div>
            </PopoverContent>
          </Popover>

          {/* Search Button */}
          <Button
            onClick={handleSearch}
            className="rounded-full h-12 w-12 bg-rose-500 hover:bg-rose-600 text-white"
            size="icon"
          >
            <Search className="h-4 w-4" />
          </Button>
        </div>
      </div>
    </div>
  );
}
</action>
