from pydantic import BaseModel, Field
from typing import Optional
from datetime import datetime


class Coordinates(BaseModel):
    """Geographic coordinates for property locations"""
    latitude: float = Field(..., ge=-90, le=90, description="Latitude (-90 to 90)")
    longitude: float = Field(..., ge=-180, le=180, description="Longitude (-180 to 180)")

    class Config:
        json_schema_extra = {
            "example": {
                "latitude": 40.7128,
                "longitude": -74.0060
            }
        }


class Location(BaseModel):
    """Complete location information for properties"""
    address: str = Field(..., min_length=1, max_length=500, description="Full street address")
    city: str = Field(..., min_length=1, max_length=100, description="City name")
    state: str = Field(..., min_length=1, max_length=100, description="State/Province")
    country: str = Field(..., min_length=1, max_length=100, description="Country name")
    postal_code: str = Field(..., min_length=3, max_length=20, description="Postal/ZIP code")
    coordinates: Coordinates = Field(..., description="Geographic coordinates")

    class Config:
        json_schema_extra = {
            "example": {
                "address": "123 Main Street",
                "city": "New York",
                "state": "NY",
                "country": "United States",
                "postal_code": "10001",
                "coordinates": {
                    "latitude": 40.7128,
                    "longitude": -74.0060
                }
            }
        }


class LocationQuery(BaseModel):
    """Search query parameters for location-based searches"""
    city: Optional[str] = Field(None, min_length=1, max_length=100, description="City to search in")
    state: Optional[str] = Field(None, min_length=1, max_length=100, description="State to search in")
    country: Optional[str] = Field(None, min_length=1, max_length=100, description="Country to search in")
    near_coordinates: Optional[Coordinates] = Field(None, description="Search near these coordinates")
    radius_km: Optional[float] = Field(None, ge=0, le=100, description="Search radius in kilometers")

    class Config:
        json_schema_extra = {
            "example": {
                "city": "New York",
                "state": "NY",
                "country": "United States",
                "near_coordinates": {
                    "latitude": 40.7128,
                    "longitude": -74.0060
                },
                "radius_km": 10.0
            }
        }
