}

export enum Amenity {
  WIFI = "WIFI",
  KITCHEN = "KITCHEN",
  PARKING = "PARKING",
  POOL = "POOL",
  GYM = "GYM",
  AIR_CONDITIONING = "AIR_CONDITIONING",
  HEATING = "HEATING",
  WASHER = "WASHER",
  DRYER = "DRYER",
  TV = "TV",
  IRON = "IRON",
  HAIR_DRYER = "HAIR_DRYER",
  ESSENTIALS = "ESSENTIALS",
  SHAMPOO = "SHAMPOO",
  HOT_WATER = "HOT_WATER",
  LINENS = "LINENS",
  TOWELS = "TOWELS",
  HANGERS = "HANGERS",
  FIREPLACE = "FIREPLACE",
  PRIVATE_ENTRANCE = "PRIVATE_ENTRANCE",
  SMOKE_DETECTOR = "SMOKE_DETECTOR",
  CARBON_MONOXIDE_DETECTOR = "CARBON_MONOXIDE_DETECTOR",
  FIRST_AID_KIT = "FIRST_AID_KIT",
  FIRE_EXTINGUISHER = "FIRE_EXTINGUISHER",
  LOCK_ON_BEDROOM_DOOR = "LOCK_ON_BEDROOM_DOOR",
  BEDROOM_COMFORTS = "BEDROOM_COMFORTS",
  COFFEE_MAKER = "COFFEE_MAKER",
  REFRIGERATOR = "REFRIGERATOR",
  DISHES_AND_SILVERWARE = "DISHES_AND_SILVERWARE",
  COOKING_BASICS = "COOKING_BASICS",
  OVEN = "OVEN",
  STOVE = "STOVE"
}

export interface Property {
  id: string;
  title: string;
  description: string;
  propertyType: PropertyType;
  location: Location;
  pricePerNight: number;
  maxGuests: number;
  bedrooms: number;
  bathrooms: number;
  beds: number;
  amenities: Amenity[];
  images: string[];
  host: {
    id: string;
    name: string;
    avatarUrl?: string;
    isSuperhost: boolean;
  };
  rating: number;
  reviewCount: number;
  isAvailable: boolean;
  checkInTime: string;
  checkOutTime: string;
  houseRules: string[];
  cancellationPolicy: string;
  createdAt: string;
  updatedAt: string;
}

export interface PropertySummary {
  id: string;
  title: string;
  location: Location;
  pricePerNight: number;
  maxGuests: number;
  bedrooms: number;
  bathrooms: number;
  beds: number;
  images: string[];
  rating: number;
  reviewCount: number;
  isSuperhost: boolean;
}

export interface PriceRange {
  min: number;
  max: number;
}

export interface SearchFilters {
  priceRange?: PriceRange;
  propertyTypes?: PropertyType[];
  amenities?: Amenity[];
  minRating?: number;
  maxGuests?: number;
  bedrooms?: number;
  bathrooms?: number;
  beds?: number;
  instantBook?: boolean;
  superhost?: boolean;
}

export enum SortOption {
  PRICE_LOW_TO_HIGH = "price_low_to_high",
  PRICE_HIGH_TO_LOW = "price_high_to_low",
  RATING_HIGH_TO_LOW = "rating_high_to_low",
  REVIEWS_HIGH_TO_LOW = "reviews_high_to_low",
  NEWEST = "newest"
}

export interface SearchQuery {
  location?: string;
  checkIn?: string;
  checkOut?: string;
  guests?: number;
  filters?: SearchFilters;
  sortBy?: SortOption;
  page?: number;
  limit?: number;
}

export interface SearchResult {
