import React, { useState } from 'react';
import { Search } from 'lucide-react';
import { Button } from '@/components/ui/button';
import LocationInput from './LocationInput';
import DateRangePicker from './DateRangePicker';
import GuestSelector from './GuestSelector';
import { SearchQuery } from '@/types/property';

interface SearchBarProps {
  onSearch: (query: SearchQuery) => void;
  className?: string;
}

const SearchBar: React.FC<SearchBarProps> = ({ onSearch, className = '' }) => {
  const [location, setLocation] = useState('');
  const [checkIn, setCheckIn] = useState<Date | undefined>();
  const [checkOut, setCheckOut] = useState<Date | undefined>();
  const [guests, setGuests] = useState({
    adults: 1,
    children: 0,
    infants: 0,
    pets: 0
  });

  const handleSearch = () => {
    const query: SearchQuery = {
      location,
      checkIn,
      checkOut,
      guests: guests.adults + guests.children,
      adults: guests.adults,
      children: guests.children,
      infants: guests.infants,
      pets: guests.pets
    };
    onSearch(query);
  };

  const isSearchDisabled = !location || !checkIn || !checkOut;

  return (
    <div className={`bg-white rounded-full shadow-lg p-2 flex items-center ${className}`}>
      <div className="flex-1 px-4">
        <LocationInput
          value={location}
          onChange={setLocation}
          placeholder="Search destinations"
        />
      </div>

      <div className="w-px h-8 bg-gray-200" />

      <div className="flex-1 px-4">
        <DateRangePicker
          checkIn={checkIn}
          checkOut={checkOut}
          onCheckInChange={setCheckIn}
          onCheckOutChange={setCheckOut}
        />
      </div>

      <div className="w-px h-8 bg-gray-200" />

      <div className="flex-1 px-4">
        <GuestSelector
          guests={guests}
          onChange={setGuests}
        />
      </div>

      <Button
        onClick={handleSearch}
        disabled={isSearchDisabled}
        className="rounded-full h-12 w-12 bg-rose-500 hover:bg-rose-600 disabled:bg-gray-300"
      >
        <Search className="h-5 w-5 text-white" />
      </Button>
    </div>
  );
};

export default SearchBar;
</action>

