import { useState, useCallback, useEffect } from 'react';
import { SearchFilters, SearchParams } from '@/types/property';
import { searchService } from '@/services/searchService';

interface UseSearchReturn {
  searchParams: SearchParams;
  setSearchParams: (params: SearchParams) => void;
  searchResults: any[];
  isLoading: boolean;
  error: string | null;
  performSearch: () => Promise<void>;
  updateFilters: (filters: Partial<SearchFilters>) => void;
  clearSearch: () => void;
}

export const useSearch = (): UseSearchReturn => {
  const [searchParams, setSearchParams] = useState<SearchParams>({
    location: '',
    checkIn: null,
    checkOut: null,
    guests: {
      adults: 1,
      children: 0,
      infants: 0,
      pets: 0,
    },
    filters: {
      priceRange: [0, 1000],
      propertyTypes: [],
      amenities: [],
      minRating: 0,
      instantBook: false,
    },
    sortBy: 'recommended',
  });

  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const performSearch = useCallback(async () => {
    if (!searchParams.location) return;

    setIsLoading(true);
    setError(null);

    try {
      const results = await searchService.searchProperties({
        location: searchParams.location,
        checkIn: searchParams.checkIn,
        checkOut: searchParams.checkOut,
        guests: Object.values(searchParams.guests).reduce((a, b) => a + b, 0),
        priceMin: searchParams.filters.priceRange[0],
        priceMax: searchParams.filters.priceRange[1],
        propertyTypes: searchParams.filters.propertyTypes,
        amenities: searchParams.filters.amenities,
        minRating: searchParams.filters.minRating,
        instantBook: searchParams.filters.instantBook,
        sortBy: searchParams.sortBy,
      });

      setSearchResults(results);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Search failed');
      setSearchResults([]);
    } finally {
      setIsLoading(false);
    }
  }, [searchParams]);

  const updateFilters = useCallback((filters: Partial<SearchFilters>) => {
    setSearchParams(prev => ({
      ...prev,
      filters: { ...prev.filters, ...filters },
    }));
  }, []);

  const clearSearch = useCallback(() => {
    setSearchParams({
      location: '',
      checkIn: null,
      checkOut: null,
      guests: {
        adults: 1,
        children: 0,
        infants: 0,
        pets: 0,
      },
      filters: {
        priceRange: [0, 1000],
        propertyTypes: [],
        amenities: [],
        minRating: 0,
        instantBook: false,
      },
      sortBy: 'recommended',
    });
    setSearchResults([]);
    setError(null);
  }, []);

  // Auto-search when params change
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      if (searchParams.location) {
        performSearch();
      }
    }, 500);

    return () => clearTimeout(debounceTimer);
  }, [searchParams, performSearch]);

  return {
    searchParams,
    setSearchParams,
    searchResults,
    isLoading,
    error,
    performSearch,
    updateFilters,
    clearSearch,
  };
};
