            "New York, NY": "https://images.unsplash.com/photo-1496442226666-8d4d0e62e6a9?w=400",
            "Brooklyn, NY": "https://images.unsplash.com/photo-1505761671935-60b3a7427bad?w=400",
            "Los Angeles, CA": "https://images.unsplash.com/photo-1506973035872-a4ec16b8e8d9?w=400",
            "San Francisco, CA": "https://images.unsplash.com/photo-1501594907352-04cda38ebc29?w=400",
            "Miami, FL": "https://images.unsplash.com/photo-1514214246283-d427a95c5d2f?w=400",
            "Chicago, IL": "https://images.unsplash.com/photo-1494522855154-9297ac14b55f?w=400"
        }

        for dest in destinations:
            key = f"{dest['city']}, {dest['state']}"
            dest['image_url'] = destination_images.get(key, "https://images.unsplash.com/photo-1476514525535-07fb3b4ae5f1?w=400")

        return destinations[:limit]

    def get_suggestions(self, query: str) -> List[str]:
        """Get location suggestions based on query"""
        query = query.lower()
        all_properties = self.property_service.get_all_properties()

        # Collect unique locations
        locations = set()
        for prop in all_properties:
            # Add city, state combinations
            if prop.location.city and prop.location.state:
                locations.add(f"{prop.location.city}, {prop.location.state}")
            # Add city, state, country combinations
            if prop.location.city and prop.location.state and prop.location.country:
                locations.add(f"{prop.location.city}, {prop.location.state}, {prop.location.country}")

        # Filter based on query
        filtered = [loc for loc in locations if query in loc.lower()]

        # Sort by relevance (exact match first, then starts with, then contains)
        exact_matches = [loc for loc in filtered if loc.lower() == query]
        starts_with = [loc for loc in filtered if loc.lower().startswith(query) and loc not in exact_matches]
        contains = [loc for loc in filtered if query in loc.lower() and loc not in exact_matches + starts_with]

        return exact_matches + sorted(starts_with) + sorted(contains)[:10]

