import React from 'react';
import { Heart, Star, MapPin } from 'lucide-react';
import { Property } from '@/types/property';

interface PropertyCardProps {
  property: Property;
  onFavorite?: (id: number) => void;
  isFavorite?: boolean;
}

export default function PropertyCard({ property, onFavorite, isFavorite }: PropertyCardProps) {
  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
    }).format(price);
  };

  const handleFavoriteClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    onFavorite?.(property.id);
  };

  return (
    <div className="group cursor-pointer">
      <div className="relative">
        <div className="aspect-square overflow-hidden rounded-xl bg-gray-100">
          <img
            src={property.images[0] || '/placeholder-property.jpg'}
            alt={property.title}
            className="h-full w-full object-cover object-center group-hover:scale-105 transition-transform duration-300"
          />
          <button
            onClick={handleFavoriteClick}
            className="absolute top-3 right-3 p-2 rounded-full bg-white/80 hover:bg-white transition-colors"
            aria-label="Add to favorites"
          >
            <Heart
              className={`h-4 w-4 ${isFavorite ? 'fill-red-500 text-red-500' : 'text-gray-700'}`}
            />
          </button>
          {property.superhost && (
            <div className="absolute top-3 left-3 px-2 py-1 bg-white/90 rounded-full text-xs font-medium">
              Superhost
            </div>
          )}
        </div>
      </div>

      <div className="mt-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className="text-sm font-medium text-gray-900 line-clamp-1">
              {property.location.neighborhood || property.location.city}, {property.location.state}
            </h3>
            <p className="text-sm text-gray-600 line-clamp-2 mt-1">{property.title}</p>
          </div>
          <div className="flex items-center ml-2">
            <Star className="h-3 w-3 fill-current text-gray-900" />
            <span className="text-sm font-medium ml-1">{property.rating.toFixed(2)}</span>
          </div>
        </div>

        <div className="mt-2">
          <span className="text-sm text-gray-600">
            {property.max_guests} guests ¬∑ {property.bedrooms} {property.bedrooms === 1 ? 'bedroom' : 'bedrooms'} ¬∑ {property.beds} {property.beds === 1 ? 'bed' : 'beds'} ¬∑ {property.bathrooms} {property.bathrooms === 1 ? 'bath' : 'baths'}
          </span>
        </div>

        <div className="mt-2 flex items-baseline">
          <span className="text-lg font-semibold">{formatPrice(property.price_per_night)}</span>
          <span className="text-sm text-gray-600 ml-1">night</span>
        </div>

        {property.instant_book && (
          <div className="mt-2">
            <span className="text-xs font-medium text-gray-700 bg-gray-100 px-2 py-1 rounded">
              Instant book
            </span>
          </div>
        )}
      </div>
    </div>
  );
}
</action>

