// TypeScript type definitions for todos and API responses

export enum Priority {
  LOW = "low",
  MEDIUM = "medium",
  HIGH = "high",
  URGENT = "urgent"
}

export enum TodoStatus {
  PENDING = "pending",
  IN_PROGRESS = "in_progress",
  COMPLETED = "completed",
  CANCELLED = "cancelled"
}

export interface TodoBase {
  title: string;
  description?: string;
  priority: Priority;
  due_date?: string; // ISO string format
  status: TodoStatus;
  user_id: number;
}

export interface TodoCreate extends TodoBase {}

export interface TodoUpdate {
  title?: string;
  description?: string;
  priority?: Priority;
  due_date?: string; // ISO string format
  status?: TodoStatus;
}

export interface TodoResponse extends TodoBase {
  id: number;
  created_at: string; // ISO string format
  updated_at: string; // ISO string format
  completed_at?: string; // ISO string format
}

export interface TodoListResponse {
  todos: TodoResponse[];
  total: number;
  page: number;
  per_page: number;
  has_next: boolean;
  has_prev: boolean;
}

export interface TodoFilters {
  user_id?: number;
  status?: TodoStatus;
  priority?: Priority;
  search?: string;
  page?: number;
  per_page?: number;
}

export interface UserTodoStats {
  total_todos: number;
  completed_todos: number;
  pending_todos: number;
  overdue_todos: number;
  completion_rate: number;
  priority_distribution: Record<Priority, number>;
}

// User related types
export interface UserBase {
  username: string;
  email: string;
  full_name?: string;
}

export interface UserCreate extends UserBase {
  password: string;
}

export interface UserUpdate {
  username?: string;
  email?: string;
  full_name?: string;
}

export interface UserResponse extends UserBase {
  id: number;
  is_active: boolean;
  created_at: string;
  updated_at: string;
  todo_count: number;
}

export interface UserLogin {
  username: string;
  password: string;
}

export interface Token {
  access_token: string;
  token_type: string;
  expires_in: number;
}

export interface TokenData {
  username?: string;
  user_id?: number;
}

export interface ApiError {
  detail: string;
}

export interface ApiResponse<T> {
  data?: T;
  error?: ApiError;
  message?: string;
}
