import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Trash2, Edit, Calendar, AlertCircle } from 'lucide-react';
import { TodoResponse, TodoStatus, Priority } from '@/types/todo';
import { cn } from '@/lib/utils';

interface TodoItemProps {
  todo: TodoResponse;
  onToggleComplete: (id: number) => void;
  onEdit: (todo: TodoResponse) => void;
  onDelete: (id: number) => void;
}

const priorityColors: Record<Priority, string> = {
  [Priority.LOW]: 'bg-blue-100 text-blue-800',
  [Priority.MEDIUM]: 'bg-yellow-100 text-yellow-800',
  [Priority.HIGH]: 'bg-orange-100 text-orange-800',
  [Priority.URGENT]: 'bg-red-100 text-red-800',
};

const statusColors: Record<TodoStatus, string> = {
  [TodoStatus.PENDING]: 'bg-gray-100 text-gray-800',
  [TodoStatus.IN_PROGRESS]: 'bg-blue-100 text-blue-800',
  [TodoStatus.COMPLETED]: 'bg-green-100 text-green-800',
  [TodoStatus.CANCELLED]: 'bg-red-100 text-red-800',
};

export const TodoItem: React.FC<TodoItemProps> = ({
  todo,
  onToggleComplete,
  onEdit,
  onDelete,
}) => {
  const isOverdue = todo.due_date && new Date(todo.due_date) < new Date() && todo.status !== TodoStatus.COMPLETED;
  const isCompleted = todo.status === TodoStatus.COMPLETED;

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  return (
    <Card className={cn(
      'transition-all duration-200 hover:shadow-md',
      isCompleted && 'opacity-75',
      isOverdue && !isCompleted && 'border-red-200'
    )}>
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-start space-x-3">
            <Checkbox
              checked={isCompleted}
              onCheckedChange={() => onToggleComplete(todo.id)}
              className="mt-1"
            />
            <CardTitle className={cn(
              'text-lg font-medium',
              isCompleted && 'line-through text-gray-500'
            )}>
              {todo.title}
            </CardTitle>
          </div>
          <div className="flex space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => onEdit(todo)}
              className="h-8 w-8 p-0"
            >
              <Edit className="h-4 w-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => onDelete(todo.id)}
              className="h-8 w-8 p-0 text-red-600 hover:text-red-700"
            >
              <Trash2 className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {todo.description && (
          <p className="text-sm text-gray-600 mb-3">{todo.description}</p>
        )}

        <div className="flex flex-wrap gap-2 items-center">
          <Badge className={priorityColors[todo.priority]}>
            {todo.priority}
          </Badge>
          <Badge className={statusColors[todo.status]}>
            {todo.status.replace('_', ' ')}
          </Badge>

          {todo.due_date && (
            <div className={cn(
              'flex items-center text-sm',
              isOverdue && !isCompleted ? 'text-red-600 font-medium' : 'text-gray-600'
            )}>
              <Calendar className="h-4 w-4 mr-1" />
              {formatDate(todo.due_date)}
              {isOverdue && !isCompleted && (
                <AlertCircle className="h-4 w-4 ml-1" />
              )}
            </div>
          )}
        </div>

        {todo.completed_at && (
          <p className="text-xs text-gray-500 mt-2">
            Completed on {formatDate(todo.completed_at)}
          </p>
        )}
      </CardContent>
    </Card>
  );
};
