import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { Plus } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Separator } from '@/components/ui/separator'
import TodoList from '@/components/TodoList'
import TodoForm from '@/components/TodoForm'
import { todoService } from '@/services/todoService'
import { TodoResponse } from '@/types/todo'
import { useToast } from '@/components/ui/use-toast'

export default function TodosPage() {
  const [todos, setTodos] = useState<TodoResponse[]>([])
  const [showForm, setShowForm] = useState(false)
  const [editingTodo, setEditingTodo] = useState<TodoResponse | null>(null)
  const [loading, setLoading] = useState(true)
  const navigate = useNavigate()
  const { toast } = useToast()

  useEffect(() => {
    loadTodos()
  }, [])

  const loadTodos = async () => {
    try {
      setLoading(true)
      const response = await todoService.getTodos()
      setTodos(response.todos)
    } catch (error) {
      toast({
        title: "Error loading todos",
        description: "Could not fetch your todos. Please try again.",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const handleCreateTodo = async (data: any) => {
    try {
      const newTodo = await todoService.createTodo(data)
      setTodos([newTodo, ...todos])
      setShowForm(false)
      toast({
        title: "Success",
        description: "Todo created successfully",
      })
    } catch (error) {
      toast({
        title: "Error creating todo",
        description: "Could not create todo. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleUpdateTodo = async (id: number, data: any) => {
    try {
      const updatedTodo = await todoService.updateTodo(id, data)
      setTodos(todos.map(todo => todo.id === id ? updatedTodo : todo))
      setEditingTodo(null)
      toast({
        title: "Success",
        description: "Todo updated successfully",
      })
    } catch (error) {
      toast({
        title: "Error updating todo",
        description: "Could not update todo. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleDeleteTodo = async (id: number) => {
    try {
      await todoService.deleteTodo(id)
      setTodos(todos.filter(todo => todo.id !== id))
      toast({
        title: "Success",
        description: "Todo deleted successfully",
      })
    } catch (error) {
      toast({
        title: "Error deleting todo",
        description: "Could not delete todo. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleToggleComplete = async (id: number) => {
    try {
      const todo = todos.find(t => t.id === id)
      if (todo) {
        await todoService.markTodoComplete(id)
        setTodos(todos.map(t => 
          t.id === id ? { ...t, status: 'completed' } : t
        ))
      }
    } catch (error) {
      toast({
        title: "Error updating todo",
        description: "Could not mark todo as complete.",
        variant: "destructive",
      })
    }
  }

  const handleViewTodo = (id: number) => {
    navigate(`/todos/${id}`)
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Todos</h1>
          <p className="text-muted-foreground">
            Manage your tasks and stay organized
          </p>
        </div>
        <Button onClick={() => setShowForm(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Add Todo
        </Button>
      </div>

      <Separator />

      <div className="grid gap-6">
        <Card>
          <CardHeader>
            <CardTitle>Your Tasks</CardTitle>
            <CardDescription>
              {todos.length === 0 
                ? "No todos yet. Create your first todo to get started!"
                : `You have ${todos.filter(t => t.status === 'pending').length} pending tasks`
              }
            </CardDescription>
          </CardHeader>
          <CardContent>
            <TodoList
              todos={todos}
              loading={loading}
              onToggleComplete={handleToggleComplete}
              onEdit={setEditingTodo}
              onDelete={handleDeleteTodo}
              onView={handleViewTodo}
            />
          </CardContent>
        </Card>
      </div>

      {showForm && (
        <Card>
          <CardHeader>
            <CardTitle>Create New Todo</CardTitle>
            <CardDescription>Add a new task to your list</CardDescription>
          </CardHeader>
          <CardContent>
            <TodoForm
              onSubmit={handleCreateTodo}
              onCancel={() => setShowForm(false)}
            />
          </CardContent>
        </Card>
      )}

      {editingTodo && (
        <Card>
          <CardHeader>
            <CardTitle>Edit Todo</CardTitle>
            <CardDescription>Update your task details</CardDescription>
          </CardHeader>
          <CardContent>
            <TodoForm
              todo={editingTodo}
              onSubmit={(data) => handleUpdateTodo(editingTodo.id, data)}
              onCancel={() => setEditingTodo(null)}
            />
          </CardContent>
        </Card>
      )}
    </div>
  )
}
