import { useState, useEffect } from 'react';
import { TodoForm } from '@/components/TodoForm';
import { TodoList } from '@/components/TodoList';
import { todoService } from '@/services/todoService';
import { Todo, TodoCreate } from '@/types/todo';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { AlertCircle, CheckCircle2, Circle, ListTodo } from 'lucide-react';

export default function TodosPage() {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Fetch todos on component mount
  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      setLoading(true);
      const fetchedTodos = await todoService.getAllTodos();
      setTodos(fetchedTodos);
      setError(null);
    } catch (err) {
      setError('Failed to fetch todos');
      console.error('Error fetching todos:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTodo = async (title: string, description: string) => {
    try {
      const newTodo: TodoCreate = { title, description };
      const createdTodo = await todoService.createTodo(newTodo);
      setTodos([createdTodo, ...todos]);
    } catch (err) {
      setError('Failed to create todo');
      console.error('Error creating todo:', err);
    }
  };

  const handleUpdateTodo = async (id: number, completed: boolean) => {
    try {
      const updatedTodo = await todoService.updateTodo(id, { completed });
      setTodos(todos.map(todo => todo.id === id ? updatedTodo : todo));
    } catch (err) {
      setError('Failed to update todo');
      console.error('Error updating todo:', err);
    }
  };

  const handleDeleteTodo = async (id: number) => {
    try {
      await todoService.deleteTodo(id);
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (err) {
      setError('Failed to delete todo');
      console.error('Error deleting todo:', err);
    }
  };

  const handleEditTodo = async (id: number, title: string, description: string) => {
    try {
      const updatedTodo = await todoService.updateTodo(id, { title, description });
      setTodos(todos.map(todo => todo.id === id ? updatedTodo : todo));
    } catch (err) {
      setError('Failed to update todo');
      console.error('Error updating todo:', err);
    }
  };

  const completedCount = todos.filter(todo => todo.completed).length;
  const pendingCount = todos.length - completedCount;

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6 py-2">
      <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
        <div>
          <h1 className="text-2xl font-bold tracking-tight">Task Management</h1>
          <p className="text-muted-foreground">Organize your work and life</p>
        </div>
        <div className="flex gap-3">
          <div className="bg-primary/10 text-primary flex items-center gap-2 rounded-lg px-3 py-2">
            <Circle className="h-4 w-4" />
            <span className="text-sm font-medium">{pendingCount} pending</span>
          </div>
          <div className="bg-primary/10 text-primary flex items-center gap-2 rounded-lg px-3 py-2">
            <CheckCircle2 className="h-4 w-4" />
            <span className="text-sm font-medium">{completedCount} completed</span>
          </div>
        </div>
      </div>

      <div className="grid gap-6 md:grid-cols-1 lg:grid-cols-3">
        <div className="lg:col-span-1">
          <Card className="shadow-sm">
            <CardHeader className="border-b">
              <CardTitle className="flex items-center gap-2 text-lg">
                <ListTodo className="h-5 w-5" />
                Add New Task
              </CardTitle>
            </CardHeader>
            <CardContent className="p-6">
              <TodoForm onSubmit={handleCreateTodo} />
            </CardContent>
          </Card>
        </div>

        <div className="lg:col-span-2">
          {error && (
            <Alert variant="destructive" className="mb-6 border-destructive/50">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <Card className="shadow-sm">
            <CardHeader className="border-b">
              <CardTitle className="text-lg">Your Tasks</CardTitle>
            </CardHeader>
            <CardContent className="p-0">
              <TodoList
                todos={todos}
                onUpdate={handleUpdateTodo}
                onDelete={handleDeleteTodo}
                onEdit={handleEditTodo}
              />
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}
</action>

