import { User, UserCreate, UserLogin } from '@/types/auth'

const API_BASE = import.meta.env.VITE_API_URL || 'http://localhost:8003/api'

export interface AuthResponse {
  user: User
  message: string
}

export interface SignupResponse {
  email: string
  id: string
  name: string
  is_active: boolean
}

class AuthService {
  async signup(userData: UserCreate): Promise<SignupResponse> {
    const response = await fetch(`${API_BASE}/auth/signup`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    })

    if (!response.ok) {
      const error = await response.json()
      throw new Error(error.detail || 'Failed to signup')
    }

    return response.json()
  }

  async login(credentials: UserLogin): Promise<AuthResponse> {
    const response = await fetch(`${API_BASE}/auth/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    })

    if (!response.ok) {
      const error = await response.json()
      throw new Error(error.detail || 'Failed to login')
    }

    return response.json()
  }

  async getCurrentUser(): Promise<User> {
    // In a real app, you would include the auth token in the headers
    // For now, we'll just return a mock user
    return {
      id: '1',
      email: 'user@example.com',
      name: 'Current User',
      is_active: true
    }
  }
}

export default new AuthService()
