import { api } from './api';
import { Lead, Deal, CreateLeadRequest, UpdateLeadRequest, CreateDealRequest, UpdateDealRequest, LeadFilters } from '../types/lead';

class LeadsService {
  // Lead operations
  async getLeads(filters?: LeadFilters): Promise<Lead[]> {
    const params = new URLSearchParams();
    if (filters) {
      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined) {
          params.append(key, String(value));
        }
      });
    }
    return api.get<Lead[]>(`/leads?${params.toString()}`);
  }

  async getLead(id: number): Promise<Lead> {
    return api.get<Lead>(`/leads/${id}`);
  }

  async createLead(data: CreateLeadRequest): Promise<Lead> {
    return api.post<Lead>('/leads', data);
  }

  async updateLead(id: number, data: UpdateLeadRequest): Promise<Lead> {
    return api.put<Lead>(`/leads/${id}`, data);
  }

  async deleteLead(id: number): Promise<void> {
    await api.delete(`/leads/${id}`);
  }

  async assignLead(id: number, userId: number): Promise<Lead> {
