import React from 'react';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { cn } from '@/lib/utils';
import { User } from '@/types/user';

interface UserAvatarProps {
  user?: User;
  name?: string;
  email?: string;
  avatar?: string;
  size?: 'sm' | 'md' | 'lg' | 'xl';
  className?: string;
  showRole?: boolean;
}

const sizeClasses = {
  sm: 'h-8 w-8 text-xs',
  md: 'h-10 w-10 text-sm',
  lg: 'h-12 w-12 text-base',
  xl: 'h-16 w-16 text-lg',
};

const roleColors = {
  ADMIN: 'border-red-500',
  PROJECT_MANAGER: 'border-blue-500',
  SALES_REP: 'border-green-500',
  DESIGNER: 'border-purple-500',
};

export function UserAvatar({ 
  user, 
  name, 
  email, 
  avatar, 
  size = 'md', 
  className, 
  showRole = false 
}: UserAvatarProps) {
  const displayName = user?.name || name || 'Unknown';
  const displayEmail = user?.email || email || '';
  const displayAvatar = user?.avatar || avatar;
  const role = user?.role;

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(part => part.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const getRoleColor = (role?: string) => {
    if (!role) return '';
    return roleColors[role as keyof typeof roleColors] || 'border-gray-500';
  };

  return (
    <div className={cn('flex items-center space-x-3', className)}>
      <Avatar className={cn(sizeClasses[size], showRole && `border-2 ${getRoleColor(role)}`)}>
        <AvatarImage src={displayAvatar} alt={displayName} />
        <AvatarFallback className={cn(sizeClasses[size])}>
          {getInitials(displayName)}
        </AvatarFallback>
      </Avatar>
      {(displayName || displayEmail) && (
        <div className="flex flex-col">
          <span className="text-sm font-medium">{displayName}</span>
          {displayEmail && (
            <span className="text-xs text-muted-foreground">{displayEmail}</span>
          )}
          {showRole && role && (
            <span className="text-xs text-muted-foreground">{role.replace('_', ' ')}</span>
          )}
        </div>
      )}
    </div>
  );
}

export function UserAvatarStack({ users, max = 3 }: { users: User[]; max?: number }) {
  const displayUsers = users.slice(0, max);
  const remaining = users.length - max;

  return (
    <div className="flex -space-x-2">
      {displayUsers.map((user) => (
        <UserAvatar
          key={user.id}
          user={user}
          size="sm"
          className="border-2 border-background"
        />
      ))}
      {remaining > 0 && (
        <div className={cn(
          sizeClasses.sm,
          "flex items-center justify-center rounded-full bg-muted text-xs font-medium border-2 border-background"
        )}>
          +{remaining}
        </div>
      )}
    </div>
  );
}
