import React from 'react';
import { Lead } from '@/types/lead';
import { StatusBadge } from '@/components/ui/status-badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { formatCurrency } from '@/lib/utils';
import { Target, DollarSign, Users } from 'lucide-react';

interface LeadPipelineProps {
  leads: Lead[];
}

export function LeadPipeline({ leads }: LeadPipelineProps) {
  const pipelineStages = [
    { status: 'cold', label: 'Cold Leads', color: 'bg-blue-500' },
    { status: 'warm', label: 'Warm Leads', color: 'bg-yellow-500' },
    { status: 'negotiation', label: 'Negotiation', color: 'bg-orange-500' },
    { status: 'won', label: 'Closed Won', color: 'bg-green-500' },
    { status: 'lost', label: 'Closed Lost', color: 'bg-red-500' },
  ];

  const getLeadsByStatus = (status: string) => {
    return leads.filter(lead => lead.status === status);
  };

  const getTotalValue = (leads: Lead[]) => {
    return leads.reduce((sum, lead) => sum + lead.budget, 0);
  };

  return (
    <div className="grid gap-4 md:grid-cols-5">
      {pipelineStages.map((stage) => {
        const stageLeads = getLeadsByStatus(stage.status);
        const totalValue = getTotalValue(stageLeads);

        return (
          <div key={stage.status} className="space-y-2">
            <div className={`${stage.color} text-white p-3 rounded-lg`}>
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">{stage.label}</span>
                <span className="text-lg font-bold">{stageLeads.length}</span>
              </div>
              <div className="text-sm opacity-90">
                {formatCurrency(totalValue)}
              </div>
            </div>

            <div className="space-y-2">
              {stageLeads.map((lead) => (
                <Card key={lead.id} className="hover:shadow-md transition-shadow">
                  <CardContent className="p-3">
                    <h4 className="font-medium text-sm mb-1">{lead.title}</h4>
                    <p className="text-xs text-muted-foreground mb-2">
                      {lead.client?.name}
                    </p>
