  TrendingUp,
  Plus,
  Eye,
  Calendar
} from 'lucide-react';

interface DashboardStats {
  totalProjects: number;
  activeProjects: number;
  completedProjects: number;
  totalRevenue: number;
  overdueTasks: number;
}

export default function PMDashboard() {
  const [user, setUser] = useState<User | null>(null);
  const [projects, setProjects] = useState<Project[]>([]);
  const [stats, setStats] = useState<DashboardStats>({
    totalProjects: 0,
    activeProjects: 0,
    completedProjects: 0,
    totalRevenue: 0,
    overdueTasks: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      const [currentUser, allProjects] = await Promise.all([
        authService.getCurrentUser(),
        projectsService.getProjects()
      ]);

      setUser(currentUser);

      // Filter projects where current user is PM
      const pmProjects = allProjects.filter(project => project.pm_id === currentUser.id);
      setProjects(pmProjects);

      // Calculate stats
      const activeProjects = pmProjects.filter(p => p.status !== 'delivered');
      const completedProjects = pmProjects.filter(p => p.status === 'delivered');
      const totalRevenue = completedProjects.reduce((sum, project) => sum + project.budget, 0);

      // Calculate overdue tasks
      const now = new Date();
      const overdueTasks = pmProjects.reduce((count, project) => {
        return count + project.tasks?.filter(task => 
          new Date(task.due_date) < now && task.status !== 'done'
        ).length || 0;
      }, 0);

      setStats({
        totalProjects: pmProjects.length,
        activeProjects: activeProjects.length,
        completedProjects: completedProjects.length,
        totalRevenue: totalRevenue,
        overdueTasks: overdueTasks
      });
    } catch (error) {
      console.error('Error loading dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRecentProjects = () => {
    return projects
      .sort((a, b) => new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime())
      .slice(0, 5);
  };

  const getProjectProgress = (project: Project) => {
    const tasks = project.tasks || [];
    if (tasks.length === 0) return 0;

    const completedTasks = tasks.filter(task => task.status === 'done').length;
    return Math.round((completedTasks / tasks.length) * 100);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
