import { authService } from '@/services/auth';
import { Plus, X } from 'lucide-react';

interface TeamAssignmentProps {
  project: Project;
  onUpdate: () => void;
}

export function TeamAssignment({ project, onUpdate }: TeamAssignmentProps) {
  const [availableUsers, setAvailableUsers] = useState<User[]>([]);
  const [selectedUser, setSelectedUser] = useState<string>('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadAvailableUsers();
  }, []);

  const loadAvailableUsers = async () => {
    try {
      const currentUser = await authService.getCurrentUser();

      const mockUsers: User[] = [
        { 
          id: 1, 
          name: 'John Designer', 
          email: 'john@example.com', 
          role: 'DESIGNER', 
          is_active: true, 
          is_deleted: false,
          created_at: '', 
          updated_at: '' 
        },
        { 
          id: 2, 
          name: 'Sarah Designer', 
          email: 'sarah@example.com', 
          role: 'DESIGNER', 
          is_active: true, 
          is_deleted: false,
          created_at: '', 
          updated_at: '' 
        },
        { 
          id: 3, 
          name: 'Mike Developer', 
          email: 'mike@example.com', 
          role: 'DESIGNER', 
          is_active: true, 
          is_deleted: false,
          created_at: '', 
          updated_at: '' 
        },
      ];

      const filteredUsers = mockUsers.filter(
        user => user.id !== currentUser.id && 
        !project.team_members?.some(member => member.id === user.id)
      );

      setAvailableUsers(filteredUsers);
    } catch (error) {
      console.error('Error loading users:', error);
    }
  };

  const handleAddMember = async () => {
    if (!selectedUser) return;

    try {
      setLoading(true);
      const userId = parseInt(selectedUser);
      const currentMembers = project.team_members?.map(m => m.id) || [];

      await projectsService.assignTeam(project.id, [...currentMembers, userId]);

      setSelectedUser('');
      onUpdate();
    } catch (error) {
      console.error('Error adding team member:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveMember = async (userId: number) => {
    try {
      setLoading(true);
      const currentMembers = project.team_members?.map(m => m.id) || [];
      const updatedMembers = currentMembers.filter(id => id !== userId);

      await projectsService.assignTeam(project.id, updatedMembers);
      onUpdate();
    } catch (error) {
      console.error('Error removing team member:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoleColor = (role: string) => {
    const colors = {
      DESIGNER: 'bg-purple-100 text-purple-800',
      PROJECT_MANAGER: 'bg-blue-100 text-blue-800',
      SALES_REP: 'bg-green-100 text-green-800',
      ADMIN: 'bg-red-100 text-red-800',
    };
    return colors[role as keyof typeof colors] || 'bg-gray-100 text-gray-800';
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Team Assignment</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <div className="flex space-x-2">
            <Select value={selectedUser} onValueChange={setSelectedUser}>
              <SelectTrigger className="flex-1">
                <SelectValue placeholder="Select team member" />
              </SelectTrigger>
              <SelectContent>
                {availableUsers.map(user => (
                  <SelectItem key={user.id} value={user.id.toString()}>
                    <div className="flex items-center space-x-2">
                      <UserAvatar name={user.name} email={user.email} size="sm" />
                      <span>{user.name}</span>
                      <Badge className={getRoleColor(user.role)}>{user.role}</Badge>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Button onClick={handleAddMember} disabled={!selectedUser || loading}>
              <Plus className="w-4 h-4 mr-2" />
              Add
            </Button>
          </div>

          <div>
            <h4 className="font-medium mb-2">Current Team</h4>
            <div className="space-y-2">
              {project.team_members?.map(member => (
                <div key={member.id} className="flex items-center justify-between p-3 border rounded-lg">
                  <UserAvatar
                    name={member.name}
                    email={member.email}
                    size="sm"
                    showRole
                  />
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => handleRemoveMember(member.id)}
                    disabled={loading}
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              ))}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}
