} from 'lucide-react';

interface DashboardStats {
  totalTasks: number;
  completedTasks: number;
  inProgressTasks: number;
  overdueTasks: number;
}

export default function DesignerDashboard() {
  const [user, setUser] = useState<User | null>(null);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [stats, setStats] = useState<DashboardStats>({
    totalTasks: 0,
    completedTasks: 0,
    inProgressTasks: 0,
    overdueTasks: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      const [currentUser, allTasks] = await Promise.all([
        authService.getCurrentUser(),
        projectsService.getTasks()
      ]);

      setUser(currentUser);

      // Filter tasks assigned to current user
      const userTasks = allTasks.filter(task => task.assigned_to_id === currentUser.id);
      setTasks(userTasks);

      // Calculate stats
      const now = new Date();
      const completedTasks = userTasks.filter(task => task.status === 'done').length;
      const inProgressTasks = userTasks.filter(task => task.status === 'in_progress').length;
      const overdueTasks = userTasks.filter(task => 
        new Date(task.due_date) < now && task.status !== 'done'
      ).length;

      setStats({
        totalTasks: userTasks.length,
        completedTasks,
        inProgressTasks,
        overdueTasks
      });
    } catch (error) {
      console.error('Error loading dashboard:', error);
    } finally {
      setLoading(false);
