export default function DesignerTasks() {
  const [user, setUser] = useState<User | null>(null);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const navigate = useNavigate();

  useEffect(() => {
    loadTasks();
  }, [filterStatus]);

  const loadTasks = async () => {
    try {
      setLoading(true);
      const [currentUser, allTasks] = await Promise.all([
        authService.getCurrentUser(),
        projectsService.getTasks()
      ]);

      setUser(currentUser);

      // Filter tasks assigned to current user
      const userTasks = allTasks.filter(task => task.assigned_to_id === currentUser.id);

      const filteredTasks = filterStatus !== 'all' 
        ? userTasks.filter(t => t.status === filterStatus)
        : userTasks;

      setTasks(filteredTasks);
    } catch (error) {
      console.error('Error loading tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
