import React, { useState, useEffect } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { api } from '@/services/api';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Card } from '@/components/ui/card';
import { formatDistanceToNow } from 'date-fns';
import { MessageCircle, Send, Edit, Trash2, Reply, Lock } from 'lucide-react';
import { toast } from 'sonner';

interface Comment {
  id: number;
  content: string;
  entity_type: string;
  entity_id: number;
  is_internal: boolean;
  created_at: string;
  updated_at: string;
  user: {
    id: number;
    name: string;
    email: string;
    role: string;
  };
  replies: Comment[];
}

interface CommentsProps {
  entityType: 'lead' | 'project' | 'task';
  entityId: number;
  allowInternal?: boolean;
}

export function Comments({ entityType, entityId, allowInternal = false }: CommentsProps) {
  const { user } = useAuth();
  const [comments, setComments] = useState<Comment[]>([]);
  const [newComment, setNewComment] = useState('');
  const [isInternal, setIsInternal] = useState(false);
  const [loading, setLoading] = useState(false);
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editingContent, setEditingContent] = useState('');
  const [replyingTo, setReplyingTo] = useState<number | null>(null);
  const [replyContent, setReplyContent] = useState('');

  useEffect(() => {
    fetchComments();
  }, [entityType, entityId]);

  const fetchComments = async () => {
    try {
      const response = await api.get(`/comments?entity_type=${entityType}&entity_id=${entityId}&include_internal=${allowInternal}`);
      setComments(response.data);
    } catch (error) {
      console.error('Failed to fetch comments:', error);
      toast.error('Failed to load comments');
    }
  };

  const handleSubmitComment = async () => {
    if (!newComment.trim()) return;

    setLoading(true);
    try {
      await api.post('/comments', {
        content: newComment,
        entity_type: entityType,
        entity_id: entityId,
        is_internal: isInternal
      });

      setNewComment('');
      setIsInternal(false);
      fetchComments();
      toast.success('Comment added successfully');
    } catch (error) {
      console.error('Failed to add comment:', error);
      toast.error('Failed to add comment');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateComment = async (commentId: number) => {
    if (!editingContent.trim()) return;

    try {
      await api.put(`/comments/${commentId}`, {
        content: editingContent,
        is_internal: isInternal
      });

      setEditingId(null);
      setEditingContent('');
      fetchComments();
      toast.success('Comment updated successfully');
    } catch (error) {
      console.error('Failed to update comment:', error);
      toast.error('Failed to update comment');
    }
  };

  const handleDeleteComment = async (commentId: number) => {
    try {
      await api.delete(`/comments/${commentId}`);
      fetchComments();
      toast.success('Comment deleted successfully');
    } catch (error) {
      console.error('Failed to delete comment:', error);
      toast.error('Failed to delete comment');
    }
  };

  const handleReply = async (parentId: number) => {
    if (!replyContent.trim()) return;

    try {
      await api.post(`/comments/${parentId}/reply`, {
        content: replyContent,
        is_internal: isInternal
      });

      setReplyingTo(null);
      setReplyContent('');
      fetchComments();
      toast.success('Reply added successfully');
    } catch (error) {
      console.error('Failed to add reply:', error);
      toast.error('Failed to add reply');
    }
  };

  const canEdit = (comment: Comment) => {
    return user?.id === comment.user.id || ['PROJECT_MANAGER', 'ADMIN'].includes(user?.role || '');
