import { toast } from 'sonner';
import { cn } from '@/lib/utils';

interface Notification {
  id: number;
  user_id: number;
  type: string;
  title: string;
  message: string;
  is_read: boolean;
  entity_type?: string;
  entity_id?: number;
  action_url?: string;
  created_at: string;
}

interface NotificationsProps {
  showUnreadOnly?: boolean;
  limit?: number;
}

export function Notifications({ showUnreadOnly = false, limit = 50 }: NotificationsProps) {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [loading, setLoading] = useState(false);
  const [stats, setStats] = useState<{ total: number; unread: number }>({ total: 0, unread: 0 });

  useEffect(() => {
    fetchNotifications();
    fetchStats();
  }, [showUnreadOnly]);

  const fetchNotifications = async () => {
    setLoading(true);
    try {
      const response = await api.get(`/notifications?unread_only=${showUnreadOnly}&limit=${limit}`);
      setNotifications(response.data as Notification[]);
    } catch (error) {
      console.error('Failed to fetch notifications:', error);
      toast.error('Failed to load notifications');
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await api.get('/notifications/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Failed to fetch notification stats:', error);
    }
  };

  const markAsRead = async (notificationId: number) => {
    try {
      await api.put(`/notifications/${notificationId}/read`);
      setNotifications(prev =>
        prev.map(n => n.id === notificationId ? { ...n, is_read: true } : n)
      );
      setStats(prev => ({ ...prev, unread: Math.max(0, prev.unread - 1) }));
    } catch (error) {
      console.error('Failed to mark as read:', error);
      toast.error('Failed to mark notification as read');
    }
  };

  const markAllAsRead = async () => {
    try {
      await api.put('/notifications/read-all');
      setNotifications(prev => prev.map(n => ({ ...n, is_read: true })));
      setStats(prev => ({ ...prev, unread: 0 }));
      toast.success('All notifications marked as read');
    } catch (error) {
      console.error('Failed to mark all as read:', error);
      toast.error('Failed to mark all notifications as read');
    }
  };

  const deleteNotification = async (notificationId: number) => {
    try {
      await api.delete(`/notifications/${notificationId}`);
      setNotifications(prev => prev.filter(n => n.id !== notificationId));
      setStats(prev => ({
        ...prev,
        total: prev.total - 1,
        unread: Math.max(0, prev.unread - 1)
      }));
      toast.success('Notification deleted');
    } catch (error) {
      console.error('Failed to delete notification:', error);
      toast.error('Failed to delete notification');
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'comment':
        return 'ðŸ’¬';
      case 'task_assigned':
        return 'ðŸ“‹';
      case 'task_updated':
        return 'âœï¸';
      case 'project_updated':
        return 'ðŸ“Š';
      case 'lead_assigned':
        return 'ðŸŽ¯';
      case 'lead_status_changed':
        return 'ðŸ”„';
      case 'milestone_reached':
        return 'ðŸŽ‰';
      case 'deadline_approaching':
        return 'â°';
      default:
        return 'ðŸ””';
    }
  };

  const getNotificationColor = (type: string) => {
    switch (type) {
      case 'comment':
        return 'bg-blue-100 text-blue-800';
      case 'task_assigned':
      case 'task_updated':
        return 'bg-purple-100 text-purple-800';
      case 'project_updated':
        return 'bg-green-100 text-green-800';
      case 'lead_assigned':
      case 'lead_status_changed':
        return 'bg-orange-100 text-orange-800';
      case 'milestone_reached':
        return 'bg-yellow-100 text-yellow-800';
      case 'deadline_approaching':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <Card className="p-4">
        <div className="animate-pulse space-y-3">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
          <div className="h-4 bg-gray-200 rounded w-2/3"></div>
        </div>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Bell className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Notifications</h3>
          {stats.unread > 0 && (
            <Badge variant="destructive" className="ml-2">
              {stats.unread}
            </Badge>
          )}
        </div>

        {stats.unread > 0 && (
          <Button
            size="sm"
            variant="outline"
            onClick={markAllAsRead}
          >
            <CheckCheck className="h-4 w-4 mr-2" />
            Mark all as read
          </Button>
        )}
      </div>

      {notifications.length === 0 ? (
        <Card className="p-8 text-center">
          <Bell className="h-12 w-12 mx-auto text-gray-400 mb-4" />
          <p className="text-gray-500">No notifications</p>
        </Card>
      ) : (
        <div className="space-y-2">
          {notifications.map((notification) => (
            <Card
              key={notification.id}
              className={cn(
                "p-4 transition-all hover:shadow-md",
                !notification.is_read && "bg-blue-50 border-blue-200"
              )}
            >
              <div className="flex items-start space-x-3">
                <Avatar className="h-10 w-10">
                  <AvatarImage src={`https://api.dicebear.com/7.x/initials/svg?seed=${notification.type}`} />
                  <AvatarFallback>
                    {getNotificationIcon(notification.type)}
                  </AvatarFallback>
                </Avatar>

                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <h4 className="text-sm font-medium text-gray-900">
                      {notification.title}
                    </h4>

                    <div className="flex items-center space-x-1">
                      {!notification.is_read && (
                        <Badge variant="secondary" className="text-xs">
                          New
                        </Badge>
                      )}

                      <span className="text-xs text-gray-500">
                        {formatDistanceToNow(new Date(notification.created_at), { addSuffix: true })}
                      </span>
                    </div>
                  </div>

                  <p className="text-sm text-gray-600 mt-1">
                    {notification.message}
                  </p>

                  <div className="flex items-center space-x-2 mt-3">
                    {!notification.is_read && (
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => markAsRead(notification.id)}
                      >
                        <Check className="h-4 w-4 mr-1" />
                        Mark as read
                      </Button>
                    )}

                    {notification.action_url && (
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => window.open(notification.action_url, '_blank')}
                      >
                        <ExternalLink className="h-4 w-4 mr-1" />
                        View
                      </Button>
                    )}

                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => deleteNotification(notification.id)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}
