  const fetchComments = async () => {
    try {
      const response = await api.get(`/comments?entity_type=${entityType}&entity_id=${entityId}&include_internal=${allowInternal}`);
      setComments(response.data as Comment[]);
    } catch (error) {
      console.error('Failed to fetch comments:', error);
      toast.error('Failed to load comments');
    }
  };

  const handleSubmitComment = async () => {
    if (!newComment.trim()) return;

    setLoading(true);
    try {
      await api.post('/comments', {
        content: newComment,
        entity_type: entityType,
        entity_id: entityId,
        is_internal: isInternal
      });

      setNewComment('');
      setIsInternal(false);
      fetchComments();
      toast.success('Comment added successfully');
    } catch (error) {
      console.error('Failed to add comment:', error);
      toast.error('Failed to add comment');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateComment = async (commentId: number) => {
    if (!editingContent.trim()) return;

    try {
      await api.put(`/comments/${commentId}`, {
        content: editingContent,
        is_internal: isInternal
      });

      setEditingId(null);
      setEditingContent('');
      fetchComments();
      toast.success('Comment updated successfully');
    } catch (error) {
      console.error('Failed to update comment:', error);
      toast.error('Failed to update comment');
    }
  };

  const handleDeleteComment = async (commentId: number) => {
    try {
      await api.delete(`/comments/${commentId}`);
      fetchComments();
      toast.success('Comment deleted successfully');
    } catch (error) {
      console.error('Failed to delete comment:', error);
      toast.error('Failed to delete comment');
    }
  };

  const handleReply = async (parentId: number) => {
    if (!replyContent.trim()) return;

    try {
      await api.post(`/comments/${parentId}/reply`, {
        content: replyContent,
        is_internal: isInternal
      });

      setReplyingTo(null);
      setReplyContent('');
      fetchComments();
      toast.success('Reply added successfully');
    } catch (error) {
      console.error('Failed to add reply:', error);
      toast.error('Failed to add reply');
    }
  };

  const canEdit = (comment: Comment) => {
    return user?.id === comment.user.id || ['PROJECT_MANAGER', 'ADMIN'].includes(user?.role || '');
  };

  const canDelete = (comment: Comment) => {
    return user?.id === comment.user.id || ['PROJECT_MANAGER', 'ADMIN'].includes(user?.role || '');
  };

  const renderComment = (comment: Comment, isReply = false) => (
    <div key={comment.id} className={`${isReply ? 'ml-8' : ''} mb-4`}>
      <Card className="p-4">
        <div className="flex items-start space-x-3">
          <Avatar className="h-8 w-8">
            <AvatarImage src={`https://api.dicebear.com/7.x/initials/svg?seed=${comment.user.name}`} />
            <AvatarFallback>{comment.user.name.charAt(0)}</AvatarFallback>
          </Avatar>

          <div className="flex-1">
            <div className="flex items-center space-x-2">
              <span className="font-medium text-sm">{comment.user.name}</span>
              <span className="text-xs text-gray-500">{comment.user.role}</span>
              {comment.is_internal && (
                <Badge variant="secondary" className="text-xs">
                  <Lock className="h-3 w-3 mr-1" />
                  Internal
                </Badge>
              )}
            </div>

            <div className="text-xs text-gray-500 mt-1">
              {formatDistanceToNow(new Date(comment.created_at), { addSuffix: true })}
            </div>

            {editingId === comment.id ? (
              <div className="mt-2">
                <Textarea
                  value={editingContent}
                  onChange={(e) => setEditingContent(e.target.value)}
                  className="min-h-[60px]"
                />
                <div className="flex space-x-2 mt-2">
                  <Button
                    size="sm"
                    onClick={() => handleUpdateComment(comment.id)}
                  >
                    Save
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => {
                      setEditingId(null);
                      setEditingContent('');
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            ) : (
              <p className="text-sm text-gray-700 mt-2 whitespace-pre-wrap">
                {comment.content}
              </p>
            )}

            <div className="flex items-center space-x-2 mt-2">
              {!isReply && (
                <Button
                  size="sm"
                  variant="ghost"
                  onClick={() => setReplyingTo(comment.id)}
                >
                  <Reply className="h-4 w-4 mr-1" />
                  Reply
                </Button>
              )}

              {canEdit(comment) && (
                <Button
                  size="sm"
                  variant="ghost"
                  onClick={() => {
                    setEditingId(comment.id);
                    setEditingContent(comment.content);
                    setIsInternal(comment.is_internal);
                  }}
                >
                  <Edit className="h-4 w-4" />
                </Button>
              )}

              {canDelete(comment) && (
                <Button
                  size="sm"
                  variant="ghost"
                  onClick={() => handleDeleteComment(comment.id)}
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              )}
            </div>

            {replyingTo === comment.id && (
              <div className="mt-4">
                <Textarea
                  value={replyContent}
                  onChange={(e) => setReplyContent(e.target.value)}
                  placeholder="Write a reply..."
                  className="min-h-[60px]"
                />
                <div className="flex space-x-2 mt-2">
                  <Button
                    size="sm"
                    onClick={() => handleReply(comment.id)}
                  >
                    <Send className="h-4 w-4 mr-1" />
                    Reply
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => {
                      setReplyingTo(null);
                      setReplyContent('');
                    }}
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            )}
          </div>
        </div>
      </Card>

      {comment.replies.map(reply => renderComment(reply, true))}
    </div>
  );

  return (
    <div className="space-y-4">
      <div className="flex items-center space-x-2">
        <MessageCircle className="h-5 w-5" />
        <h3 className="text-lg font-semibold">Comments & Notes</h3>
      </div>

      <div className="space-y-4">
        {comments.map(comment => renderComment(comment))}
      </div>

      <Card className="p-4">
        <div className="space-y-4">
          <Textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Add a comment or note..."
            className="min-h-[80px]"
          />

          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {allowInternal && (
                <label className="flex items-center space-x-2 text-sm">
                  <input
                    type="checkbox"
                    checked={isInternal}
                    onChange={(e) => setIsInternal(e.target.checked)}
                    className="rounded"
                  />
                  <span>Internal note only</span>
                </label>
              )}
            </div>

            <Button
              onClick={handleSubmitComment}
              disabled={!newComment.trim() || loading}
            >
              <Send className="h-4 w-4 mr-2" />
              Post Comment
            </Button>
          </div>
        </div>
      </Card>
    </div>
  );
}
</action>

