import React, { useState, useEffect } from 'react';
import { api } from '@/services/api';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { formatCurrency } from '@/lib/utils';
import { Trophy, TrendingUp, TrendingDown } from 'lucide-react';

interface TeamMember {
  user_id: number;
  name: string;
  email: string;
  total_leads: number;
  converted_deals: number;
  total_revenue: number;
  conversion_rate: number;
  average_deal_value: number;
  total_projects?: number;
  total_tasks?: number;
  completed_tasks?: number;
  completion_rate?: number;
}

interface TeamPerformanceProps {
  teamType: 'sales' | 'design';
  months?: number;
}

export function TeamPerformance({ teamType, months = 6 }: TeamPerformanceProps) {
  const [teamData, setTeamData] = useState<TeamMember[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTeamPerformance();
  }, [teamType, months]);

  const fetchTeamPerformance = async () => {
    try {
      const endpoint = teamType === 'sales' 
        ? `/analytics/performance/sales-team?months=${months}`
        : `/analytics/performance/design-team?months=${months}`;

      const response = await api.get(endpoint);
      setTeamData(response.data as TeamMember[]);
    } catch (error) {
      console.error('Failed to fetch team performance:', error);
    } finally {
      setLoading(false);
    }
  };

  const getPerformanceBadge = (value: number, type: 'rate' | 'revenue' | 'count') => {
    if (type === 'rate') {
      if (value >= 50) return <Badge className="bg-green-100 text-green-800">{value.toFixed(1)}%</Badge>;
      if (value >= 30) return <Badge className="bg-yellow-100 text-yellow-800">{value.toFixed(1)}%</Badge>;
      return <Badge className="bg-red-100 text-red-800">{value.toFixed(1)}%</Badge>;
    }

    if (type === 'revenue') {
      if (value >= 50000) return <Badge className="bg-green-100 text-green-800">{formatCurrency(value)}</Badge>;
      if (value >= 20000) return <Badge className="bg-yellow-100 text-yellow-800">{formatCurrency(value)}</Badge>;
      return <Badge className="bg-red-100 text-red-800">{formatCurrency(value)}</Badge>;
    }

    return <Badge>{value}</Badge>;
  };

  const getRankIcon = (index: number) => {
    if (index === 0) return <Trophy className="h-4 w-4 text-yellow-500" />;
    if (index === 1) return <Trophy className="h-4 w-4 text-gray-400" />;
    if (index === 2) return <Trophy className="h-4 w-4 text-orange-600" />;
    return null;
  };

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>
            {teamType === 'sales' ? 'Sales Team Performance' : 'Design Team Performance'}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {[1, 2, 3].map(i => (
              <div key={i} className="h-16 bg-gray-100 animate-pulse rounded" />
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle>
          {teamType === 'sales' ? 'Sales Team Performance' : 'Design Team Performance'}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead className="w-12">Rank</TableHead>
              <TableHead>Name</TableHead>
              {teamType === 'sales' ? (
                <>
                  <TableHead>Leads</TableHead>
                  <TableHead>Deals</TableHead>
                  <TableHead>Revenue</TableHead>
                  <TableHead>Conversion</TableHead>
                  <TableHead>Avg Deal</TableHead>
                </>
              ) : (
                <>
                  <TableHead>Projects</TableHead>
                  <TableHead>Tasks</TableHead>
                  <TableHead>Completed</TableHead>
                  <TableHead>Completion Rate</TableHead>
                </>
              )}
            </TableRow>
          </TableHeader>
          <TableBody>
            {teamData
              .sort((a, b) => {
                if (teamType === 'sales') {
                  return b.total_revenue - a.total_revenue;
                } else {
                  return (b.completion_rate || 0) - (a.completion_rate || 0);
                }
              })
              .map((member, index) => (
                <TableRow key={member.user_id}>
                  <TableCell>
                    <div className="flex items-center space-x-1">
                      <span>{index + 1}</span>
                      {getRankIcon(index)}
                    </div>
                  </TableCell>
                  <TableCell className="font-medium">{member.name}</TableCell>

                  {teamType === 'sales' ? (
                    <>
                      <TableCell>{member.total_leads}</TableCell>
                      <TableCell>{member.converted_deals}</TableCell>
                      <TableCell>{formatCurrency(member.total_revenue)}</TableCell>
                      <TableCell>
                        {getPerformanceBadge(member.conversion_rate, 'rate')}
                      </TableCell>
                      <TableCell>{formatCurrency(member.average_deal_value)}</TableCell>
                    </>
                  ) : (
                    <>
                      <TableCell>{member.total_projects || 0}</TableCell>
                      <TableCell>{member.total_tasks || 0}</TableCell>
                      <TableCell>{member.completed_tasks || 0}</TableTableCell>
                      <TableCell>
                        {getPerformanceBadge(member.completion_rate || 0, 'rate')}
                      </TableCell>
                    </>
                  )}
                </TableRow>
              ))}
          </TableBody>
        </Table>

        {teamData.length === 0 && (
          <div className="text-center py-8 text-gray-500">
            No performance data available
          </div>
        )}
      </CardContent>
    </Card>
  );
}
</action>

