  Activity
} from 'lucide-react';
import {
  Sidebar,
  SidebarContent,
  SidebarFooter,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarHeader,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from '@/components/ui/sidebar';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';

interface User {
  id: number;
  name: string;
  email: string;
  role: 'ADMIN' | 'PROJECT_MANAGER' | 'SALES_REP' | 'DESIGNER';
  avatar?: string;
}

interface NavItem {
  title: string;
  url: string;
  icon: React.ElementType;
  badge?: string;
}

interface NavGroup {
  title: string;
  items: NavItem[];
}

export function AppSidebar() {
  const [user, setUser] = useState<User | null>(null);
  const [notifications, setNotifications] = useState(0);
  const location = useLocation();

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  const isActiveRoute = (url: string) => {
    if (url === "/") return location.pathname === "/";
    return location.pathname === url || location.pathname.startsWith(url + "/");
  };

  const getNavigationForRole = (role: string): NavGroup[] => {
    const baseNavigation: NavGroup[] = [
      {
        title: "Overview",
        items: [
          { title: "Dashboard", url: "/", icon: Home },
        ]
      }
    ];

    switch (role) {
      case 'SALES_REP':
        return [
          ...baseNavigation,
          {
            title: "Sales",
            items: [
              { title: "Leads", url: "/sales/leads", icon: Target },
              { title: "Deals", url: "/sales/deals", icon: DollarSign },
              { title: "Clients", url: "/sales/clients", icon: Users },
            ]
          },
          {
            title: "Analytics",
            items: [
              { title: "Performance", url: "/analytics/sales", icon: TrendingUp },
            ]
          }
        ];

      case 'PROJECT_MANAGER':
        return [
          ...baseNavigation,
          {
            title: "Projects",
            items: [
              { title: "All Projects", url: "/pm/projects", icon: Briefcase },
              { title: "Tasks", url: "/pm/tasks", icon: CheckSquare },
              { title: "Team", url: "/pm/team", icon: Users },
            ]
          },
          {
            title: "Analytics",
            items: [
              { title: "Overview", url: "/analytics", icon: BarChart3 },
              { title: "Team Performance", url: "/analytics/team", icon: TrendingUp },
            ]
          }
        ];

      case 'DESIGNER':
        return [
          ...baseNavigation,
          {
            title: "Work",
            items: [
              { title: "My Tasks", url: "/designer/tasks", icon: CheckSquare },
              { title: "Projects", url: "/designer/projects", icon: Briefcase },
              { title: "Time Tracking", url: "/designer/time", icon: Clock },
            ]
          }
        ];

      case 'ADMIN':
        return [
          ...baseNavigation,
          {
            title: "Sales",
            items: [
              { title: "Leads", url: "/sales/leads", icon: Target },
              { title: "Deals", url: "/sales/deals", icon: DollarSign },
              { title: "Clients", url: "/sales/clients", icon: Users },
            ]
          },
          {
            title: "Projects",
            items: [
              { title: "All Projects", url: "/pm/projects", icon: Briefcase },
              { title: "Tasks", url: "/pm/tasks", icon: CheckSquare },
              { title: "Team", url: "/pm/team", icon: Users },
            ]
          },
          {
            title: "Analytics",
            items: [
              { title: "Overview", url: "/analytics", icon: BarChart3 },
              { title: "Reports", url: "/analytics/reports", icon: FileText },
              { title: "Team Performance", url: "/analytics/team", icon: TrendingUp },
            ]
          },
          {
            title: "Management",
            items: [
              { title: "Users", url: "/admin/users", icon: Users },
              { title: "Settings", url: "/settings", icon: Settings },
            ]
          }
        ];

      default:
        return baseNavigation;
    }
  };

  const navigation = user ? getNavigationForRole(user.role) : getNavigationForRole('');

  return (
    <Sidebar>
      <SidebarHeader>
        <div className="flex items-center space-x-2 px-4 py-3">
          <div className="flex h-8 w-8 items-center justify-center rounded-lg bg-primary text-primary-foreground">
            <Briefcase className="h-4 w-4" />
          </div>
          <span className="font-semibold">CRM Platform</span>
        </div>
      </SidebarHeader>

      <SidebarContent>
        {navigation.map((group) => (
          <SidebarGroup key={group.title}>
            <SidebarGroupLabel>{group.title}</SidebarGroupLabel>
            <SidebarGroupContent>
              <SidebarMenu>
                {group.items.map((item) => (
                  <SidebarMenuItem key={item.title}>
                    <SidebarMenuButton
                      asChild
                      isActive={isActiveRoute(item.url)}
                    >
                      <Link to={item.url}>
                        <item.icon className="h-4 w-4" />
                        <span>{item.title}</span>
                        {item.badge && (
                          <Badge className="ml-auto" variant="secondary">
                            {item.badge}
                          </Badge>
                        )}
                      </Link>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                ))}
              </SidebarMenu>
            </SidebarGroupContent>
          </SidebarGroup>
        ))}
      </SidebarContent>

      <SidebarFooter>
        {user && (
          <div className="p-4">
            <div className="flex items-center space-x-3">
              <Avatar className="h-8 w-8">
                <AvatarImage src={user.avatar || `https://api.dicebear.com/7.x/initials/svg?seed=${user.name}`} />
                <AvatarFallback>{user.name.charAt(0)}</AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <p className="text-sm font-medium">{user.name}</p>
                <p className="text-xs text-gray-500">{user.role.replace('_', ' ')}</p>
              </div>
            </div>
          </div>
        )}
      </SidebarFooter>
    </Sidebar>
  );
