import React, { useState, useRef, useEffect, useCallback } from 'react';
import { EditorContent, useEditor, Editor } from '@tiptap/react';
import { Extension } from '@tiptap/core';
import StarterKit from '@tiptap/starter-kit';
import { Placeholder } from '@tiptap/extension-placeholder';
import { Underline } from '@tiptap/extension-underline';
import { TextAlign } from '@tiptap/extension-text-align';
import { Highlight } from '@tiptap/extension-highlight';
import { TaskList } from '@tiptap/extension-task-list';
import { TaskItem } from '@tiptap/extension-task-item';
import { Image } from '@tiptap/extension-image';
import { Link } from '@tiptap/extension-link';
import { CodeBlockLowlight } from '@tiptap/extension-code-block-lowlight';
import { lowlight } from 'lowlight';
import { BubbleMenu } from '@tiptap/react';
import { cn } from '@/lib/utils';
import { Toolbar } from './Toolbar';
import { BlockRenderer } from './BlockRenderer';
import { ContentBlock, BlockType } from '@/types/note';
import { Loader2 } from 'lucide-react';

interface RichTextEditorProps {
  content: ContentBlock[];
  onChange: (content: ContentBlock[]) => void;
  onSave?: (content: ContentBlock[]) => void;
  placeholder?: string;
  className?: string;
  readOnly?: boolean;
  autoFocus?: boolean;
}

interface SlashCommandItem {
  title: string;
  command: (editor: Editor) => void;
  icon: string;
  keywords: string[];
}

const slashCommands: SlashCommandItem[] = [
  {
    title: 'Heading 1',
    command: (editor) => editor.chain().focus().toggleHeading({ level: 1 }).run(),
    icon: 'H1',
    keywords: ['h1', 'heading', 'title'],
  },
  {
    title: 'Heading 2',
    command: (editor) => editor.chain().focus().toggleHeading({ level: 2 }).run(),
    icon: 'H2',
    keywords: ['h2', 'heading', 'subtitle'],
  },
  {
    title: 'Heading 3',
    command: (editor) => editor.chain().focus().toggleHeading({ level: 3 }).run(),
    icon: 'H3',
    keywords: ['h3', 'heading', 'subheading'],
  },
  {
    title: 'Bullet List',
    command: (editor) => editor.chain().focus().toggleBulletList().run(),
    icon: '‚Ä¢',
    keywords: ['bullet', 'list', 'ul'],
  },
  {
    title: 'Numbered List',
    command: (editor) => editor.chain().focus().toggleOrderedList().run(),
    icon: '1.',
    keywords: ['numbered', 'list', 'ol'],
  },
  {
    title: 'Task List',
    command: (editor) => editor.chain().focus().toggleTaskList().run(),
    icon: '‚òê',
    keywords: ['task', 'todo', 'checkbox'],
  },
  {
    title: 'Quote',
    command: (editor) => editor.chain().focus().toggleBlockquote().run(),
    icon: '"',
    keywords: ['quote', 'blockquote'],
  },
  {
    title: 'Code',
    command: (editor) => editor.chain().focus().toggleCode().run(),
    icon: '</>',
    keywords: ['code', 'inline'],
  },
  {
    title: 'Code Block',
    command: (editor) => editor.chain().focus().toggleCodeBlock().run(),
    icon: '[]',
    keywords: ['codeblock', 'pre'],
  },
];

const SlashCommand = Extension.create({
  name: 'slashCommand',
  addOptions() {
    return {
      suggestion: {
        items: ({ query }: { query: string }) => {
          return slashCommands.filter((item) =>
            item.title.toLowerCase().includes(query.toLowerCase()) ||
            item.keywords.some(keyword => keyword.includes(query.toLowerCase()))
          );
        },
        command: ({ editor, range, props }: { editor: Editor; range: any; props: any }) => {
          props.command(editor);
        },
      },
    };
  },
});

export const RichTextEditor: React.FC<RichTextEditorProps> = ({
  content,
  onChange,
  onSave,
  placeholder = "Start writing...",
  className,
  readOnly = false,
  autoFocus = false,
}) => {
  const [isSaving, setIsSaving] = useState(false);
  const [showSlashMenu, setShowSlashMenu] = useState(false);
  const [slashQuery, setSlashQuery] = useState('');
  const saveTimeoutRef = useRef<NodeJS.Timeout>();

  const editor = useEditor({
    extensions: [
      StarterKit.configure({
        heading: {
          levels: [1, 2, 3],
        },
        bulletList: {
          keepMarks: true,
          keepAttributes: false,
        },
        orderedList: {
          keepMarks: true,
          keepAttributes: false,
        },
      }),
      Underline,
      TextAlign.configure({
        types: ['heading', 'paragraph'],
      }),
      Highlight,
      TaskList,
      TaskItem.configure({
        nested: true,
      }),
      Image,
      Link.configure({
        openOnClick: false,
        autolink: true,
        defaultProtocol: 'https',
      }),
      CodeBlockLowlight.configure({
        lowlight,
      }),
      Placeholder.configure({
        placeholder,
      }),
      SlashCommand,
    ],
    content: convertBlocksToTiptap(content),
    editable: !readOnly,
    autofocus: autoFocus ? 'start' : false,
    onUpdate: ({ editor }) => {
      const blocks = convertTiptapToBlocks(editor.getJSON());
      onChange(blocks);

      if (onSave) {
        if (saveTimeoutRef.current) {
          clearTimeout(saveTimeoutRef.current);
        }

        saveTimeoutRef.current = setTimeout(() => {
          setIsSaving(true);
          onSave(blocks).finally(() => setIsSaving(false));
        }, 1000);
      }
    },
    onCreate: ({ editor }) => {
      if (autoFocus && !readOnly) {
        editor.commands.focus('start');
      }
    },
  });

  useEffect(() => {
    if (editor && content.length > 0) {
      const currentContent = convertTiptapToBlocks(editor.getJSON());
      const hasChanged = JSON.stringify(currentContent) !== JSON.stringify(content);

      if (hasChanged) {
        editor.commands.setContent(convertBlocksToTiptap(content));
      }
    }
  }, [content, editor]);

  useEffect(() => {
    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current);
      }
    };
  }, []);

  const handleKeyDown = useCallback((event: KeyboardEvent) => {
    if (event.key === '/' && !showSlashMenu) {
      setShowSlashMenu(true);
    }
  }, [showSlashMenu]);

  const handleSlashCommand = useCallback((command: SlashCommandItem) => {
    if (editor) {
      command.command(editor);
      setShowSlashMenu(false);
      setSlashQuery('');
    }
  }, [editor]);

  const filteredCommands = slashCommands.filter((item) =>
    item.title.toLowerCase().includes(slashQuery.toLowerCase()) ||
    item.keywords.some(keyword => keyword.includes(slashQuery.toLowerCase()))
  );

  if (!editor) {
    return (
      <div className={cn("flex items-center justify-center p-8", className)}>
        <Loader2 className="h-6 w-6 animate-spin text-muted-foreground" />
      </div>
    );
  }

  return (
    <div className={cn("relative", className)}>
      {!readOnly && (
        <>
          <Toolbar editor={editor} />

          {editor && (
            <BubbleMenu
              editor={editor}
              tippyOptions={{ duration: 100 }}
              className="bg-popover border rounded-lg shadow-lg p-1 flex gap-1"
            >
              <button
                onClick={() => editor.chain().focus().toggleBold().run()}
                className={cn(
                  "p-2 rounded hover:bg-accent",
                  editor.isActive('bold') && "bg-accent"
                )}
              >
                <strong>B</strong>
              </button>
              <button
                onClick={() => editor.chain().focus().toggleItalic().run()}
                className={cn(
                  "p-2 rounded hover:bg-accent",
                  editor.isActive('italic') && "bg-accent"
                )}
              >
                <em>I</em>
              </button>
              <button
                onClick={() => editor.chain().focus().toggleUnderline().run()}
                className={cn(
                  "p-2 rounded hover:bg-accent underline",
                  editor.isActive('underline') && "bg-accent"
                )}
              >
                U
              </button>
              <button
                onClick={() => editor.chain().focus().toggleHighlight().run()}
                className={cn(
                  "p-2 rounded hover:bg-accent",
                  editor.isActive('highlight') && "bg-accent"
                )}
              >
                <span className="bg-yellow-200 px-1">H</span>
              </button>
            </BubbleMenu>
          )}
        </>
      )}

      <div className="relative">
        <EditorContent
          editor={editor}
          className={cn(
            "prose prose-sm sm:prose-base max-w-none",
            "focus:outline-none",
            "min-h-[200px] p-4",
            readOnly && "prose-p:cursor-default"
          )}
        />

        {isSaving && (
          <div className="absolute top-2 right-2 text-xs text-muted-foreground flex items-center gap-1">
            <Loader2 className="h-3 w-3 animate-spin" />
            Saving...
          </div>
        )}
      </div>

      {showSlashMenu && (
        <div className="absolute z-10 bg-popover border rounded-lg shadow-lg p-2 w-64">
          <input
            type="text"
            placeholder="Type to filter..."
            value={slashQuery}
            onChange={(e) => setSlashQuery(e.target.value)}
            className="w-full px-2 py-1 text-sm border rounded mb-2"
            autoFocus
          />
          <div className="max-h-64 overflow-y-auto">
            {filteredCommands.map((command) => (
              <button
                key={command.title}
                onClick={() => handleSlashCommand(command)}
                className="w-full text-left px-2 py-1.5 text-sm hover:bg-accent rounded flex items-center gap-2"
              >
                <span className="text-lg">{command.icon}</span>
                <span>{command.title}</span>
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

// Helper functions
function convertBlocksToTiptap(blocks: ContentBlock[]): any {
  if (!blocks || blocks.length === 0) {
    return {
      type: 'doc',
      content: [
        {
          type: 'paragraph',
          content: [],
        },
      ],
    };
  }

  const content = blocks.map((block) => {
    const baseContent = {
      type: 'paragraph',
      content: [
        {
          type: 'text',
          text: block.content.text || '',
        },
      ],
    };

    switch (block.type) {
      case BlockType.HEADING_1:
        return {
          type: 'heading',
          attrs: { level: 1 },
          content: [{ type: 'text', text: block.content.text || '' }],
        };
      case BlockType.HEADING_2:
        return {
          type: 'heading',
          attrs: { level: 2 },
          content: [{ type: 'text', text: block.content.text || '' }],
        };
      case BlockType.HEADING_3:
        return {
          type: 'heading',
          attrs: { level: 3 },
          content: [{ type: 'text', text: block.content.text || '' }],
        };
      case BlockType.BULLETED_LIST:
        return {
          type: 'bulletList',
          content: [
            {
              type: 'listItem',
              content: [
                {
                  type: 'paragraph',
                  content: [{ type: 'text', text: block.content.text || '' }],
                },
              ],
            },
          ],
        };
      case BlockType.NUMBERED_LIST:
        return {
          type: 'orderedList',
          content: [
            {
              type: 'listItem',
              content: [
                {
                  type: 'paragraph',
                  content: [{ type: 'text', text: block.content.text || '' }],
                },
              ],
            },
          ],
        };
      case BlockType.TODO:
        return {
          type: 'taskList',
          content: [
            {
              type: 'taskItem',
              attrs: { checked: block.content.checked || false },
              content: [
                {
                  type: 'paragraph',
                  content: [{ type: 'text', text: block.content.text || '' }],
                },
              ],
            },
          ],
        };
      case BlockType.QUOTE:
        return {
          type: 'blockquote',
          content: [
            {
              type: 'paragraph',
              content: [{ type: 'text', text: block.content.text || '' }],
            },
          ],
        };
      case BlockType.CODE:
        return {
          type: 'codeBlock',
          attrs: { language: block.content.language || 'text' },
          content: [{ type: 'text', text: block.content.text || '' }],
        };
      default:
        return baseContent;
    }
  });

  return {
    type: 'doc',
    content,
  };
}

function convertTiptapToTiptap(content: any): ContentBlock[] {
  if (!content || !content.content) return [];

  return content.content.map((node: any, index: number) => {
    const baseBlock: ContentBlock = {
      id: `block-${index}`,
      type: BlockType.PARAGRAPH,
      content: { text: '' },
      order: index,
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
    };

    switch (node.type) {
      case 'heading':
        const level = node.attrs?.level || 1;
        return {
          ...baseBlock,
          type: level === 1 ? BlockType.HEADING_1 : level === 2 ? BlockType.HEADING_2 : BlockType.HEADING_3,
          content: { text: extractText(node) },
        };
      case 'bulletList':
        return {
          ...baseBlock,
          type: BlockType.BULLETED_LIST,
          content: { text: extractText(node) },
        };
      case 'orderedList':
        return {
          ...baseBlock,
          type: BlockType.NUMBERED_LIST,
          content: { text: extractText(node) },
        };
      case 'taskList':
        return {
          ...baseBlock,
          type: BlockType.TODO,
          content: {
            text: extractText(node),
            checked: node.content?.[0]?.attrs?.checked || false,
          },
        };
      case 'blockquote':
        return {
          ...baseBlock,
          type: BlockType.QUOTE,
          content: { text: extractText(node) },
        };
      case 'codeBlock':
        return {
          ...baseBlock,
          type: BlockType.CODE,
          content: {
            text: extractText(node),
            language: node.attrs?.language || 'text',
          },
        };
      case 'paragraph':
      default:
        return {
          ...baseBlock,
          content: { text: extractText(node) },
        };
    }
  });
}

function extractText(node: any): string {
  if (!node.content) return '';

  return node.content
    .map((child: any) => {
      if (child.type === 'text') {
        return child.text || '';
      }
      if (child.content) {
        return extractText(child);
      }
      return '';
    })
    .join('');
}

