import { useState, useEffect, useRef, useCallback } from 'react'
import { useNotes } from '@/hooks/useNotes'
import { NoteResponse } from '@/types/note'
import { Button } from '@/components/ui/button'
import { Textarea } from '@/components/ui/textarea'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Card } from '@/components/ui/card'
import { Separator } from '@/components/ui/separator'
import { toast } from 'sonner'
import { 
  Bold, Italic, List, ListOrdered, Link, Code, Quote, Save, Star, Pin, Trash2, Archive
} from 'lucide-react'
import { cn } from '@/lib/utils'

interface NoteEditorProps {
  note: NoteResponse | null
  onClose?: () => void
}

export default function NoteEditor({ note, onClose }: NoteEditorProps) {
  const { updateNote, archiveNote, deleteNote } = useNotes()
  const [title, setTitle] = useState(note?.title || '')
  const [content, setContent] = useState(note?.content || '')
  const [tags, setTags] = useState<string[]>(note?.tags || [])
  const [isSaving, setIsSaving] = useState(false)
  const [lastSaved, setLastSaved] = useState<Date | null>(null)
  const saveTimeoutRef = useRef<NodeJS.Timeout>()

  // Auto-save functionality
  useEffect(() => {
    if (!note) return

    const hasChanges = title !== note.title || content !== note.content || 
                      JSON.stringify(tags) !== JSON.stringify(note.tags)

    if (hasChanges) {
      setIsSaving(true)

      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current)
      }

      saveTimeoutRef.current = setTimeout(async () => {
        try {
          await updateNote({ id: note.id, data: { title, content, tags } })
          setLastSaved(new Date())
          setIsSaving(false)
        } catch (error) {
          setIsSaving(false)
          toast.error('Failed to save changes')
        }
      }, 1000)
    }

    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current)
      }
    }
  }, [title, content, tags, note, updateNote])

  // Update local state when note changes
  useEffect(() => {
    if (note) {
      setTitle(note.title)
      setContent(note.content || '')
      setTags(note.tags || [])
      setLastSaved(new Date(note.updated_at))
    }
  }, [note])

  const handleFormat = useCallback((format: string) => {
    const textarea = document.getElementById('content-editor') as HTMLTextAreaElement
    if (!textarea) return

    const start = textarea.selectionStart
    const end = textarea.selectionEnd
    const selectedText = content.substring(start, end)
    let newText = content

    switch (format) {
      case 'bold':
        newText = content.substring(0, start) + `**${selectedText}**` + content.substring(end)
        break
      case 'italic':
        newText = content.substring(0, start) + `*${selectedText}*` + content.substring(end)
        break
      case 'list':
        newText = content.substring(0, start) + `\n- ${selectedText}` + content.substring(end)
        break
      case 'ordered':
        newText = content.substring(0, start) + `\n1. ${selectedText}` + content.substring(end)
        break
      case 'quote':
        newText = content.substring(0, start) + `\n> ${selectedText}` + content.substring(end)
        break
      case 'code':
        newText = content.substring(0, start) + `\`${selectedText}\`` + content.substring(end)
        break
    }

    setContent(newText)
    textarea.focus()
  }, [content])

  const handleAddTag = useCallback((tag: string) => {
    const trimmed = tag.trim()
    if (trimmed && !tags.includes(trimmed)) {
      setTags([...tags, trimmed])
    }
  }, [tags])

  const handleRemoveTag = useCallback((tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove))
  }, [tags])

  const handleArchive = useCallback(async () => {
    if (!note) return
    try {
      await archiveNote(note.id)
      toast.success('Note archived')
      onClose?.()
    } catch (error) {
      toast.error('Failed to archive note')
    }
  }, [note, archiveNote, onClose])

  const handleDelete = useCallback(async () => {
    if (!note) return
    try {
      await deleteNote(note.id)
      toast.success('Note deleted')
      onClose?.()
    } catch (error) {
      toast.error('Failed to delete note')
    }
  }, [note, deleteNote, onClose])

  if (!note) {
    return (
      <div className="flex-1 flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="text-6xl mb-4">üìù</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">Select a note to start writing</h3>
          <p className="text-sm text-gray-500">Choose a note from the sidebar or create a new one</p>
        </div>
      </div>
    )
  }

  return (
    <div className="flex-1 flex flex-col bg-white">
      {/* Header */}
      <div className="border-b px-6 py-4">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => updateNote({ id: note.id, data: { is_favorite: !note.is_favorite } })}
            >
              <Star className={cn("h-4 w-4", note.is_favorite && "fill-yellow-400 text-yellow-400")} />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => updateNote({ id: note.id, data: { is_pinned: !note.is_pinned } })}
            >
              <Pin className={cn("h-4 w-4", note.is_pinned && "fill-current")} />
            </Button>
          </div>

          <div className="flex items-center space-x-2">
            {isSaving && (
              <div className="flex items-center text-sm text-gray-500">
                <Save className="h-4 w-4 mr-1 animate-pulse" />
                Saving...
              </div>
            )}
            {lastSaved && !isSaving && (
              <div className="text-sm text-gray-500">
                Saved {lastSaved.toLocaleTimeString()}
              </div>
            )}

            <Button variant="ghost" size="sm" onClick={handleArchive}>
              <Archive className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm" onClick={handleDelete}>
              <Trash2 className="h-4 w-4" />
            </Button>
          </div>
        </div>

        <Input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Note title..."
          className="text-2xl font-bold border-none p-0 focus:ring-0"
        />
      </div>

      {/* Toolbar */}
      <div className="border-b px-6 py-3">
        <div className="flex items-center space-x-1">
          <Button variant="ghost" size="sm" onClick={() => handleFormat('bold')}>
            <Bold className="h-4 w-4" />
          </Button>
          <Button variant="ghost" size="sm" onClick={() => handleFormat('italic')}>
            <Italic className="h-4 w-4" />
          </Button>
          <Separator orientation="vertical" className="h-4 mx-2" />
          <Button variant="ghost" size="sm" onClick={() => handleFormat('list')}>
            <List className="h-4 w-4" />
          </Button>
          <Button variant="ghost" size="sm" onClick={() => handleFormat('ordered')}>
            <ListOrdered className="h-4 w-4" />
          </Button>
          <Button variant="ghost" size="sm" onClick={() => handleFormat('quote')}>
            <Quote className="h-4 w-4" />
          </Button>
          <Button variant="ghost" size="sm" onClick={() => handleFormat('code')}>
            <Code className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto">
        <div className="px-6 py-4">
          <Textarea
            id="content-editor"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Start writing your note..."
            className="min-h-[400px] border-none p-0 focus:ring-0 resize-none text-base leading-relaxed"
          />
        </div>

        {/* Tags */}
        <div className="px-6 pb-4">
          <div className="flex items-center space-x-2 mb-2">
            <span className="text-sm font-medium text-gray-700">Tags:</span>
            <Input
              placeholder="Add tag..."
              className="h-8 text-sm"
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  e.preventDefault()
                  handleAddTag(e.currentTarget.value)
                  e.currentTarget.value = ''
                }
              }}
            />
          </div>
          <div className="flex flex-wrap gap-2">
            {tags.map(tag => (
              <Badge key={tag} variant="secondary" className="text-xs">
                {tag}
                <button
                  onClick={() => handleRemoveTag(tag)}
                  className="ml-1 hover:text-red-500"
                >
                  √ó
                </button>
              </Badge>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}
