import { useState, useCallback, useMemo } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { api, endpoints } from '@/lib/api'
import { NoteResponse, NoteCreate, NoteUpdate, NoteSearchParams, NoteStats } from '@/types/note'
import { toast } from 'sonner'

// Query keys for caching
const NOTES_KEY = 'notes'
const NOTE_STATS_KEY = 'note-stats'

interface UseNotesOptions {
  folderId?: string
  searchQuery?: string
  tags?: string[]
  priority?: string
  isPinned?: boolean
  isFavorite?: boolean
  limit?: number
}

export function useNotes(options: UseNotesOptions = {}) {
  const queryClient = useQueryClient()
  const [optimisticUpdates, setOptimisticUpdates] = useState<Map<string, Partial<NoteResponse>>>(new Map())

  // Fetch notes with caching
  const { data: notes = [], isLoading, error, refetch } = useQuery({
    queryKey: [NOTES_KEY, options],
    queryFn: async () => {
      const params = new URLSearchParams()

      if (options.searchQuery) params.append('query', options.searchQuery)
      if (options.folderId) params.append('folder_id', options.folderId)
      if (options.tags?.length) params.append('tags', options.tags.join(','))
      if (options.priority) params.append('priority', options.priority)
      if (options.isPinned !== undefined) params.append('is_pinned', String(options.isPinned))
      if (options.isFavorite !== undefined) params.append('is_favorite', String(options.isFavorite))
      if (options.limit) params.append('limit', String(options.limit))

      const response = await api.get<NoteResponse[]>(`${endpoints.notes.list}?${params}`)
      return response
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
    cacheTime: 10 * 60 * 1000, // 10 minutes
  })

  // Fetch note stats
  const { data: stats } = useQuery({
    queryKey: [NOTE_STATS_KEY],
    queryFn: () => api.get<NoteStats>(endpoints.notes.stats),
    staleTime: 30 * 1000, // 30 seconds
  })

  // Create note mutation
  const createNoteMutation = useMutation({
    mutationFn: async (data: NoteCreate) => {
      return api.post<NoteResponse>(endpoints.notes.create, data)
    },
    onMutate: async (newNote) => {
      // Cancel outgoing refetches
      await queryClient.cancelQueries({ queryKey: [NOTES_KEY] })

      // Snapshot previous value
      const previousNotes = queryClient.getQueryData<NoteResponse[]>([NOTES_KEY, options])

      // Optimistically add new note
      const optimisticNote: NoteResponse = {
        ...newNote,
        id: `temp_${Date.now()}`,
        user_id: 'user_123',
        status: 'active',
        word_count: newNote.content ? newNote.content.split(/\s+/).length : 0,
        reading_time: Math.max(1, Math.floor((newNote.content ? newNote.content.split(/\s+/).length : 0) / 200)),
        created_at: new Date(),
        updated_at: new Date(),
        last_accessed_at: new Date(),
      }

      queryClient.setQueryData<NoteResponse[]>([NOTES_KEY, options], (old) => [optimisticNote, ...(old || [])])

      return { previousNotes }
    },
    onError: (error, variables, context) => {
      queryClient.setQueryData([NOTES_KEY, options], context?.previousNotes)
      toast.error('Failed to create note')
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: [NOTES_KEY] })
      queryClient.invalidateQueries({ queryKey: [NOTE_STATS_KEY] })
    },
  })

  // Update note mutation
  const updateNoteMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: NoteUpdate }) => {
      return api.put<NoteResponse>(endpoints.notes.update(id), data)
    },
    onMutate: async ({ id, data }) => {
      await queryClient.cancelQueries({ queryKey: [NOTES_KEY] })

      const previousNotes = queryClient.getQueryData<NoteResponse[]>([NOTES_KEY, options])

      // Optimistic update
      queryClient.setQueryData<NoteResponse[]>([NOTES_KEY, options], (old) =>
        old?.map(note =>
          note.id === id
            ? { ...note, ...data, updated_at: new Date() }
            : note
        ) || []
      )

      return { previousNotes }
    },
    onError: (error, variables, context) => {
      queryClient.setQueryData([NOTES_KEY, options], context?.previousNotes)
      toast.error('Failed to update note')
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: [NOTES_KEY] })
      queryClient.invalidateQueries({ queryKey: [NOTE_STATS_KEY] })
    },
  })

  // Delete note mutation
  const deleteNoteMutation = useMutation({
    mutationFn: async (id: string) => {
      await api.delete(endpoints.notes.delete(id))
    },
    onMutate: async (id) => {
      await queryClient.cancelQueries({ queryKey: [NOTES_KEY] })

      const previousNotes = queryClient.getQueryData<NoteResponse[]>([NOTES_KEY, options])

      queryClient.setQueryData<NoteResponse[]>([NOTES_KEY, options], (old) =>
        old?.filter(note => note.id !== id) || []
      )

      return { previousNotes }
    },
    onError: (error, variables, context) => {
      queryClient.setQueryData([NOTES_KEY, options], context?.previousNotes)
      toast.error('Failed to delete note')
    },
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey: [NOTES_KEY] })
      queryClient.invalidateQueries({ queryKey: [NOTE_STATS_KEY] })
    },
  })

  // Search notes
  const searchNotes = useCallback(async (query: string, limit: number = 20) => {
    if (!query.trim()) return []

    const params = new URLSearchParams({ q: query, limit: String(limit) })
    return api.get<NoteResponse[]>(`${endpoints.notes.search}?${params}`)
  }, [])

  // Archive note
  const archiveNote = useMutation({
    mutationFn: async (id: string) => {
      return api.post<NoteResponse>(endpoints.notes.archive(id), {})
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [NOTES_KEY] })
      queryClient.invalidateQueries({ queryKey: [NOTE_STATS_KEY] })
      toast.success('Note archived')
    },
  })

  // Restore note
  const restoreNote = useMutation({
    mutationFn: async (id: string) => {
      return api.post<NoteResponse>(endpoints.notes.restore(id), {})
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [NOTES_KEY] })
      queryClient.invalidateQueries({ queryKey: [NOTE_STATS_KEY] })
      toast.success('Note restored')
    },
  })

  // Get note by ID
  const getNote = useCallback(async (id: string) => {
    return api.get<NoteResponse>(endpoints.notes.get(id))
  }, [])

  // Optimistic helpers
  const optimisticUpdateNote = useCallback((id: string, updates: Partial<NoteResponse>) => {
    setOptimisticUpdates(prev => new Map(prev).set(id, updates))
  }, [])

  const clearOptimisticUpdates = useCallback(() => {
    setOptimisticUpdates(new Map())
  }, [])

  // Memoized notes with optimistic updates applied
  const notesWithOptimistic = useMemo(() => {
    if (!notes.length) return []

    return notes.map(note => {
      const optimistic = optimisticUpdates.get(note.id)
      return optimistic ? { ...note, ...optimistic } : note
    })
  }, [notes, optimisticUpdates])

  return {
    // Data
    notes: notesWithOptimistic,
    stats: stats,
    isLoading,
    error,

    // Actions
    createNote: createNoteMutation.mutateAsync,
    updateNote: updateNoteMutation.mutateAsync,
    deleteNote: deleteNoteMutation.mutateAsync,
    archiveNote: archiveNote.mutateAsync,
    restoreNote: restoreNote.mutateAsync,
    searchNotes,
    getNote,
    refetch,

    // Optimistic helpers
    optimisticUpdateNote,
    clearOptimisticUpdates,

    // Mutation states
    isCreating: createNoteMutation.isPending,
    isUpdating: updateNoteMutation.isPending,
    isDeleting: deleteNoteMutation.isPending,
  }
}
