import { useState, useCallback, useMemo } from 'react'
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { api, endpoints } from '@/lib/api'
import { FolderResponse, FolderCreate, FolderUpdate, FolderStats } from '@/types/note'
import { toast } from 'sonner'

// Query keys for caching
const FOLDERS_KEY = 'folders'
const FOLDER_STATS_KEY = 'folder-stats'

interface UseFoldersOptions {
  parentId?: string
  includeChildren?: boolean
}

export function useFolders(options: UseFoldersOptions = {}) {
  const queryClient = useQueryClient()
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(new Set())

  // Fetch folders
  const { data: folders = [], isLoading, error, refetch } = useQuery({
    queryKey: [FOLDERS_KEY, options],
    queryFn: async () => {
      const params = new URLSearchParams()
      if (options.parentId) params.append('parent_id', options.parentId)
      if (options.includeChildren !== undefined) params.append('include_children', String(options.includeChildren))

      const response = await api.get<FolderResponse[]>(`${endpoints.folders.list}?${params}`)
      return response
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
    cacheTime: 10 * 60 * 1000, // 10 minutes
  })

  // Fetch folder stats
  const { data: stats } = useQuery({
    queryKey: [FOLDER_STATS_KEY],
    queryFn: () => api.get<FolderStats>(endpoints.folders.stats),
    staleTime: 30 * 1000, // 30 seconds
  })

  // Create folder mutation
  const createFolderMutation = useMutation({
    mutationFn: async (data: FolderCreate) => {
      return api.post<FolderResponse>(endpoints.folders.create, data)
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [FOLDERS_KEY] })
      queryClient.invalidateQueries({ queryKey: [FOLDER_STATS_KEY] })
      toast.success('Folder created')
    },
    onError: () => {
      toast.error('Failed to create folder')
    },
  })

  // Update folder mutation
  const updateFolderMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: FolderUpdate }) => {
      return api.put<FolderResponse>(endpoints.folders.update(id), data)
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [FOLDERS_KEY] })
      toast.success('Folder updated')
    },
    onError: () => {
      toast.error('Failed to update folder')
    },
  })

  // Delete folder mutation
  const deleteFolderMutation = useMutation({
    mutationFn: async ({ id, force }: { id: string; force?: boolean }) => {
      const params = force ? `?force=true` : ''
      await api.delete(`${endpoints.folders.delete(id)}${params}`)
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [FOLDERS_KEY] })
      queryClient.invalidateQueries({ queryKey: [FOLDER_STATS_KEY] })
      queryClient.invalidateQueries({ queryKey: ['notes'] }) // Refresh notes to update folder_id
      toast.success('Folder deleted')
    },
    onError: (error: any) => {
      if (error.status === 400) {
        toast.error(error.message || 'Cannot delete folder with contents')
      } else {
        toast.error('Failed to delete folder')
      }
    },
  })

  // Get folder by ID
  const getFolder = useCallback(async (id: string) => {
    return api.get<FolderResponse>(endpoints.folders.get(id))
  }, [])

  // Get folder path
  const getFolderPath = useCallback(async (id: string) => {
    return api.get<FolderResponse[]>(endpoints.folders.path(id))
  }, [])

  // Build folder tree
  const buildFolderTree = useCallback((folders: FolderResponse[]): FolderResponse[] => {
    const folderMap = new Map<string, FolderResponse>()
    const rootFolders: FolderResponse[] = []

    // Create map of all folders
    folders.forEach(folder => {
      folderMap.set(folder.id, { ...folder, children: [] })
    })

    // Build tree structure
    folders.forEach(folder => {
      if (folder.parent_id && folderMap.has(folder.parent_id)) {
        const parent = folderMap.get(folder.parent_id)!
        parent.children.push(folderMap.get(folder.id)!)
      } else if (!folder.parent_id) {
        rootFolders.push(folderMap.get(folder.id)!)
      }
    })

    return rootFolders
  }, [])

  // Flatten folder tree
  const flattenFolders = useCallback((folders: FolderResponse[]): FolderResponse[] => {
    const result: FolderResponse[] = []

    function traverse(folder: FolderResponse) {
      result.push(folder)
      folder.children.forEach(traverse)
    }

    folders.forEach(traverse)
    return result
  }, [])

  // Find folder by ID in tree
  const findFolderById = useCallback((folders: FolderResponse[], id: string): FolderResponse | null => {
    for (const folder of folders) {
      if (folder.id === id) return folder
      const found = findFolderById(folder.children, id)
      if (found) return found
    }
    return null
  }, [])

  // Get folder breadcrumb
  const getFolderBreadcrumb = useCallback((folders: FolderResponse[], folderId: string): FolderResponse[] => {
    const breadcrumb: FolderResponse[] = []
    const folderMap = new Map<string, FolderResponse>()

    // Build map of all folders
    flattenFolders(folders).forEach(folder => {
      folderMap.set(folder.id, folder)
    })

    // Build breadcrumb
    let current = folderMap.get(folderId)
    while (current) {
      breadcrumb.unshift(current)
      current = current.parent_id ? folderMap.get(current.parent_id) : undefined
    }

    return breadcrumb
  }, [flattenFolders])

  // Toggle folder expansion
  const toggleFolder = useCallback((folderId: string) => {
    setExpandedFolders(prev => {
      const newSet = new Set(prev)
      if (newSet.has(folderId)) {
        newSet.delete(folderId)
      } else {
        newSet.add(folderId)
      }
      return newSet
    })
  }, [])

  // Expand all folders
  const expandAll = useCallback((folders: FolderResponse[]) => {
    const allIds = new Set<string>()
    flattenFolders(folders).forEach(folder => {
      if (folder.children.length > 0) {
        allIds.add(folder.id)
      }
    })
    setExpandedFolders(allIds)
  }, [flattenFolders, flattenFolders])

  // Collapse all folders
  const collapseAll = useCallback(() => {
    setExpandedFolders(new Set())
  }, [])

  // Check if folder is expanded
  const isExpanded = useCallback((folderId: string) => {
    return expandedFolders.has(folderId)
  }, [expandedFolders])

  // Get folder note count (including subfolders)
  const getFolderNoteCount = useCallback((folder: FolderResponse): number => {
    let count = folder.note_count
    folder.children.forEach(child => {
      count += getFolderNoteCount(child)
    })
    return count
  }, [])

  // Memoized tree structure
  const folderTree = useMemo(() => {
    return buildFolderTree(folders)
  }, [folders, buildFolderTree])

  // Memoized flat list
  const allFolders = useMemo(() => {
    return flattenFolders(folderTree)
  }, [folderTree, flattenFolders])

  return {
    // Data
    folders: folderTree,
    allFolders,
    stats,
    isLoading,
    error,
    expandedFolders,

    // Actions
    createFolder: createFolderMutation.mutateAsync,
    updateFolder: updateFolderMutation.mutateAsync,
    deleteFolder: deleteFolderMutation.mutateAsync,
    getFolder,
    getFolderPath,
    refetch,

    // Tree helpers
    buildFolderTree,
    flattenFolders,
    findFolderById,
    getFolderBreadcrumb,
    getFolderNoteCount,

    // Expansion helpers
    toggleFolder,
    expandAll,
    collapseAll,
    isExpanded,

    // Mutation states
    isCreating: createFolderMutation.isPending,
    isUpdating: updateFolderMutation.isPending,
    isDeleting: deleteFolderMutation.isPending,
  }
}
