import React from 'react';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Mail, Phone, MapPin, Calendar } from 'lucide-react';
import { User } from '@/services/auth';

interface EmployeeCardProps {
  employee: User;
  onClick?: () => void;
  showRole?: boolean;
}

export function EmployeeCard({ employee, onClick, showRole = true }: EmployeeCardProps) {
  const getRoleBadgeVariant = (role: string) => {
    switch (role) {
      case 'admin':
        return 'destructive';
      case 'manager':
        return 'default';
      case 'employee':
        return 'secondary';
      default:
        return 'outline';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  return (
    <Card 
      className="hover:shadow-lg transition-shadow cursor-pointer"
      onClick={onClick}
    >
      <CardHeader className="pb-3">
        <div className="flex items-center space-x-4">
          <Avatar className="h-12 w-12">
            <AvatarImage src={`https://api.dicebear.com/7.x/initials/svg?seed=${employee.full_name}`} />
            <AvatarFallback>
              {employee.full_name.split(' ').map(n => n[0]).join('').toUpperCase()}
            </AvatarFallback>
          </Avatar>

          <div className="flex-1">
            <h3 className="font-semibold text-lg">{employee.full_name}</h3>
            <p className="text-sm text-muted-foreground">@{employee.username}</p>
          </div>

          {showRole && (
            <Badge variant={getRoleBadgeVariant(employee.role) as any}>
              {employee.role}
            </Badge>
          )}
        </div>
      </CardHeader>

      <CardContent className="space-y-3">
        <div className="space-y-2">
          <div className="flex items-center text-sm text-muted-foreground">
            <Mail className="h-4 w-4 mr-2" />
            {employee.email}
          </div>

          <div className="flex items-center text-sm text-muted-foreground">
            <Calendar className="h-4 w-4 mr-2" />
            Joined {formatDate(employee.created_at)}
          </div>
        </div>

        <div className="flex items-center justify-between pt-2">
          <Badge 
            variant={employee.is_active ? "default" : "secondary"}
            className="text-xs"
          >
            {employee.is_active ? 'Active' : 'Inactive'}
          </Badge>
        </div>
      </CardContent>
    </Card>
  );
}
</action>

