@import 'tailwindcss/base';
@import 'tailwindcss/components';
@import 'tailwindcss/utilities';
@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;500;600;700&family=Inter:wght@300;400;500;600;700&display=swap');

@layer base {
  :root {
    /* Louis Vuitton Premium Color Palette */
    --lv-brown: 30 25% 15%;
    --lv-brown-light: 30 20% 25%;
    --lv-gold: 45 70% 55%;
    --lv-gold-light: 45 60% 65%;
    --lv-burgundy: 350 45% 25%;
    --lv-burgundy-light: 350 35% 35%;
    --lv-cream: 35 25% 95%;
    --lv-cream-dark: 35 20% 85%;
    --lv-black: 0 0% 8%;
    --lv-gray: 30 10% 45%;

    /* Theme Variables */
    --background: var(--lv-cream);
    --foreground: var(--lv-brown);
    --card: var(--lv-cream-dark);
    --card-foreground: var(--lv-brown);
    --popover: var(--lv-cream);
    --popover-foreground: var(--lv-brown);
    --primary: var(--lv-gold);
    --primary-foreground: var(--lv-brown);
    --secondary: var(--lv-brown-light);
    --secondary-foreground: var(--lv-cream);
    --muted: var(--lv-cream-dark);
    --muted-foreground: var(--lv-gray);
    --accent: var(--lv-burgundy);
    --accent-foreground: var(--lv-cream);
    --destructive: var(--lv-burgundy);
    --destructive-foreground: var(--lv-cream);
    --border: var(--lv-brown-light);
    --input: var(--lv-cream-dark);
    --ring: var(--lv-gold);
    --radius: 0.5rem;
    --chart-1: var(--lv-gold);
    --chart-2: var(--lv-burgundy);
    --chart-3: var(--lv-brown);
    --chart-4: var(--lv-gold-light);
    --chart-5: var(--lv-burgundy-light);

    /* Sidebar Variables */
    --sidebar: var(--lv-brown);
    --sidebar-foreground: var(--lv-cream);
    --sidebar-primary: var(--lv-gold);
    --sidebar-primary-foreground: var(--lv-brown);
    --sidebar-accent: var(--lv-brown-light);
    --sidebar-accent-foreground: var(--lv-cream);
    --sidebar-border: var(--lv-brown-light);
    --sidebar-ring: var(--lv-gold);
  }

  .dark {
    --background: var(--lv-brown);
    --foreground: var(--lv-cream);
    --card: var(--lv-brown-light);
    --card-foreground: var(--lv-cream);
    --popover: var(--lv-brown);
    --popover-foreground: var(--lv-cream);
    --primary: var(--lv-gold);
    --primary-foreground: var(--lv-brown);
    --secondary: var(--lv-burgundy);
    --secondary-foreground: var(--lv-cream);
    --muted: var(--lv-brown-light);
    --muted-foreground: var(--lv-cream-dark);
    --accent: var(--lv-gold);
    --accent-foreground: var(--lv-brown);
    --destructive: var(--lv-burgundy-light);
    --destructive-foreground: var(--lv-cream);
    --border: var(--lv-brown-light);
    --input: var(--lv-brown-light);
    --ring: var(--lv-gold);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', sans-serif;
    font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: 'Playfair Display', serif;
  }
}

@layer utilities {
  .animate-in {
    animation: animate-in 0.3s ease-out;
  }

  .animate-out {
    animation: animate-out 0.2s ease-in;
  }

  @keyframes animate-in {
    from {
      opacity: 0;
      transform: translateY(-10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes animate-out {
    from {
      opacity: 1;
      transform: translateY(0);
    }
    to {
      opacity: 0;
      transform: translateY(-10px);
    }
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground));
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground));
}

/* Premium gradient backgrounds */
.lv-gradient {
  background: linear-gradient(135deg, 
    hsl(var(--lv-brown)) 0%, 
    hsl(var(--lv-burgundy)) 50%, 
    hsl(var(--lv-gold)) 100%);
}

.lv-gold-gradient {
  background: linear-gradient(135deg, 
    hsl(var(--lv-gold)) 0%, 
    hsl(var(--lv-gold-light)) 100%);
}

.lv-card-gradient {
  background: linear-gradient(135deg, 
    hsl(var(--card)) 0%, 
    hsl(var(--background)) 100%);
}
