from fastapi import APIRouter, HTTPException
from typing import List
from datetime import datetime
import uuid
from models.note_models import NoteCreate, NoteUpdate, NoteResponse

router = APIRouter()

# In-memory storage for simplicity
notes_db = {}

@router.post("/notes", response_model=NoteResponse)
async def create_note(note: NoteCreate):
    note_id = str(uuid.uuid4())
    now = datetime.utcnow()

    note_data = note.dict()
    note_data.update({
        "id": note_id,
        "created_at": now,
        "updated_at": now
    })

    notes_db[note_id] = note_data
    return NoteResponse(**note_data)

@router.get("/notes", response_model=List[NoteResponse])
async def list_notes():
    return [NoteResponse(**note) for note in notes_db.values()]

@router.get("/notes/{note_id}", response_model=NoteResponse)
async def get_note(note_id: str):
    if note_id not in notes_db:
        raise HTTPException(status_code=404, detail="Note not found")
    return NoteResponse(**notes_db[note_id])

@router.put("/notes/{note_id}", response_model=NoteResponse)
async def update_note(note_id: str, note_update: NoteUpdate):
    if note_id not in notes_db:
        raise HTTPException(status_code=404, detail="Note not found")

    note_data = notes_db[note_id]
    update_data = note_update.dict(exclude_unset=True)

    for field, value in update_data.items():
        note_data[field] = value

    note_data["updated_at"] = datetime.utcnow()
    notes_db[note_id] = note_data

    return NoteResponse(**note_data)

@router.delete("/notes/{note_id}")
async def delete_note(note_id: str):
    if note_id not in notes_db:
        raise HTTPException(status_code=404, detail="Note not found")

    del notes_db[note_id]
    return {"message": "Note deleted successfully"}
