import { useState } from 'react';
import { Plus } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useNotes } from '@/hooks/use-notes';
import NotesList from '@/components/NotesList';
import CreateNoteDialog from '@/components/CreateNoteDialog';
import EditNoteDialog from '@/components/EditNoteDialog';
import EmptyState from '@/components/EmptyState';
import { Note, CreateNoteData, UpdateNoteData } from '@/types/note';

export default function NotesPage() {
  const { notes, loading, error, createNote, updateNote, deleteNote, refetch } = useNotes();
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [editingNote, setEditingNote] = useState<Note | null>(null);

  const handleCreateNote = async (data: CreateNoteData) => {
    await createNote(data);
  };

  const handleEditNote = (note: Note) => {
    setEditingNote(note);
    setEditDialogOpen(true);
  };

  const handleUpdateNote = async (id: string, data: UpdateNoteData) => {
    await updateNote(id, data);
  };

  const handleDeleteNote = async (id: string) => {
    await deleteNote(id);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-destructive mb-4">Error: {error}</p>
        <Button onClick={refetch} variant="outline">
          Try Again
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Notes</h1>
          <p className="text-muted-foreground">
            {notes.length === 0 ? 'No notes yet' : `${notes.length} note${notes.length === 1 ? '' : 's'}`}
          </p>
        </div>

        <Button onClick={() => setCreateDialogOpen(true)}>
          <Plus className="h-4 w-4 mr-2" />
          New Note
        </Button>
      </div>

      {notes.length === 0 ? (
        <EmptyState onCreateNote={() => setCreateDialogOpen(true)} />
      ) : (
        <NotesList
          notes={notes}
          onEdit={handleEditNote}
          onDelete={handleDeleteNote}
        />
      )}

      <CreateNoteDialog
        open={createDialogOpen}
        onOpenChange={setCreateDialogOpen}
        onCreate={handleCreateNote}
      />

      <EditNoteDialog
        open={editDialogOpen}
        onOpenChange={setEditDialogOpen}
        note={editingNote}
        onUpdate={handleUpdateNote}
      />
    </div>
  );
}
