import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Textarea } from '@/components/ui/textarea'
import { Label } from '@/components/ui/label'
import { Sheet, SheetContent, SheetDescription, SheetHeader, SheetTitle } from '@/components/ui/sheet'
import { useNotes } from '@/hooks/use-notes'
import { Plus } from 'lucide-react'
import { cn } from '@/lib/utils'

interface CreateNoteDialogProps {
  open: boolean
  onOpenChange: (open: boolean) => void
}

export default function CreateNoteDialog({ open, onOpenChange }: CreateNoteDialogProps) {
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const [color, setColor] = useState('#ffffff')
  const { createNote } = useNotes()

  const colors = [
    { name: 'White', value: '#ffffff' },
    { name: 'Yellow', value: '#fef3c7' },
    { name: 'Green', value: '#dcfce7' },
    { name: 'Blue', value: '#dbeafe' },
    { name: 'Purple', value: '#e9d5ff' },
    { name: 'Pink', value: '#fce7f3' },
  ]

  const resetForm = () => {
    setTitle('')
    setContent('')
    setColor('#ffffff')
  }

  const handleSubmit = async (e?: React.FormEvent) => {
    e?.preventDefault()

    if (!title.trim()) {
      setTitle('Untitled Note')
    }

    try {
      await createNote({
        title: title.trim() || 'Untitled Note',
        content: content.trim(),
        color,
      })

      resetForm()
      onOpenChange(false)
    } catch (error) {
      console.error('Failed to create note:', error)
    }
  }

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && e.metaKey) {
      handleSubmit()
    }
    if (e.key === 'Escape') {
      resetForm()
      onOpenChange(false)
    }
  }

  useEffect(() => {
    if (open) {
      setTimeout(() => {
        const titleInput = document.querySelector('[data-autofocus]') as HTMLInputElement
        titleInput?.focus()
      }, 100)
    }
  }, [open])

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent 
        side="right" 
        className="w-full sm:max-w-md p-0"
        onKeyDown={handleKeyDown}
      >
        <div className="flex flex-col h-full">
          <SheetHeader className="px-6 py-4 border-b">
            <SheetTitle>Create New Note</SheetTitle>
            <SheetDescription>
              Add a title and content to create your note
            </SheetDescription>
          </SheetHeader>

          <form onSubmit={handleSubmit} className="flex-1 overflow-y-auto">
            <div className="p-6 space-y-6">
              <div className="space-y-2">
                <Label htmlFor="title">Title</Label>
                <Input
                  id="title"
                  data-autofocus
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  placeholder="Note title..."
                  className="text-lg font-medium"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="content">Content</Label>
                <Textarea
                  id="content"
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                  placeholder="Start writing your note..."
                  className="min-h-[200px] resize-none"
                />
              </div>

              <div className="space-y-2">
                <Label>Color</Label>
                <div className="flex gap-2">
                  {colors.map((c) => (
                    <button
                      key={c.value}
                      type="button"
                      onClick={() => setColor(c.value)}
                      className={cn(
                        'w-8 h-8 rounded-full border-2 transition-all',
                        color === c.value 
                          ? 'border-gray-900 scale-110' 
                          : 'border-gray-300 hover:scale-105'
                      )}
                      style={{ backgroundColor: c.value }}
                      aria-label={`Select ${c.name} color`}
                    />
                  ))}
                </div>
              </div>
            </div>

            <div className="px-6 py-4 border-t bg-gray-50">
              <div className="flex gap-3">
                <Button 
                  type="submit" 
                  className="flex-1"
                  disabled={!title.trim() && !content.trim()}
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Create Note
                </Button>
                <Button 
                  type="button"
                  variant="outline"
                  onClick={() => {
                    resetForm()
                    onOpenChange(false)
                  }}
                >
                  Cancel
                </Button>
              </div>
            </div>
          </form>
        </div>
      </SheetContent>
    </Sheet>
  )
}

