import { useState, useEffect, useRef, useCallback } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Textarea } from '@/components/ui/textarea'
import { Trash2, Palette } from 'lucide-react'
import { useNotes } from '@/hooks/use-notes'
import { formatDistanceToNow } from 'date-fns'
import { toast } from 'sonner'
import { cn } from '@/lib/utils'

interface NoteEditorProps {
  note: {
    id: string
    title: string
    content: string
    color: string
    updated_at: string
  } | null
  onUpdate: (id: string, data: Partial<{
    title: string
    content: string
    color: string
  }>) => Promise<void>
  onDelete: (id: string) => Promise<void>
}

export default function NoteEditor({ note, onUpdate, onDelete }: NoteEditorProps) {
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const [color, setColor] = useState('#ffffff')
  const [isSaving, setIsSaving] = useState(false)
  const [lastSaved, setLastSaved] = useState<string | null>(null)
  const [showColorPicker, setShowColorPicker] = useState(false)

  const saveTimeoutRef = useRef<NodeJS.Timeout>()
  const previousContentRef = useRef({ title: '', content: '', color: '' })

  const { deleteNote } = useNotes()

  const colors = [
    { name: 'White', value: '#ffffff' },
    { name: 'Yellow', value: '#fef3c7' },
    { name: 'Green', value: '#dcfce7' },
    { name: 'Blue', value: '#dbeafe' },
    { name: 'Purple', value: '#e9d5ff' },
    { name: 'Pink', value: '#fce7f3' },
  ]

  // Update local state when note changes
  useEffect(() => {
    if (note) {
      setTitle(note.title)
      setContent(note.content)
      setColor(note.color)
      const date = new Date(note.updated_at)
      setLastSaved(formatDistanceToNow(date, { addSuffix: true }))
      previousContentRef.current = { title: note.title, content: note.content, color: note.color }
    }
  }, [note])

  // Smart auto-save with 1500ms debounce
  const debouncedSave = useCallback((newTitle: string, newContent: string, newColor: string) => {
    if (!note) return

    // Check if content actually changed
    const hasChanged = 
      newTitle !== previousContentRef.current.title ||
      newContent !== previousContentRef.current.content ||
      newColor !== previousContentRef.current.color

    if (!hasChanged) return

    // Clear previous timeout
    if (saveTimeoutRef.current) {
      clearTimeout(saveTimeoutRef.current)
    }

    // Set new timeout
    saveTimeoutRef.current = setTimeout(async () => {
      try {
        setIsSaving(true)
        await onUpdate(note.id, {
          title: newTitle,
          content: newContent,
          color: newColor,
        })

        previousContentRef.current = { title: newTitle, content: newContent, color: newColor }
        setLastSaved('just now')

        // Hide saving indicator after a short delay
        setTimeout(() => setIsSaving(false), 500)
      } catch (error) {
        console.error('Failed to save:', error)
        toast.error('Failed to save note')
        setIsSaving(false)
      }
    }, 1500)
  }, [note, onUpdate])

  // Trigger save when content changes
  useEffect(() => {
    if (note) {
      debouncedSave(title, content, color)
    }

    return () => {
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current)
      }
    }
  }, [title, content, color, debouncedSave])

  const handleDelete = async () => {
    if (!note) return

    try {
      await deleteNote(note.id)
      toast.success('Note deleted')
    } catch (error) {
      console.error('Failed to delete note:', error)
      toast.error('Failed to delete note')
    }
  }

  if (!note) {
    return (
      <div className="flex items-center justify-center h-full text-gray-500">
        <div className="text-center">
          <p className="text-lg mb-2">Select a note to start editing</p>
          <p className="text-sm">or create a new one</p>
        </div>
      </div>
    )
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header */}
      <div className="flex items-center justify-between px-6 py-4 border-b">
        <div className="flex items-center gap-3">
          <div className={cn(
            'w-2 h-2 rounded-full transition-all duration-300',
            isSaving ? 'bg-yellow-500 animate-pulse' : 'bg-green-500'
          )} />
          <span className="text-sm text-gray-600">
