import { useState, useEffect } from 'react'
import { Button } from '@/components/ui/button'
import { Plus, Menu } from 'lucide-react'
import NotesList from '@/components/NotesList'
import NoteEditor from '@/components/NoteEditor'
import CreateNoteDialog from '@/components/CreateNoteDialog'
import { useNotes } from '@/hooks/use-notes'
import { toast } from 'sonner'

// Helper function for className concatenation
function cn(...classes: (string | boolean | undefined)[]) {
  return classes.filter(Boolean).join(' ')
}

export default function NotesPage() {
  const [selectedNoteId, setSelectedNoteId] = useState<string | null>(null)
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false)
  const [isSidebarOpen, setIsSidebarOpen] = useState(true)

  const { notes, createNote, updateNote, deleteNote, isLoading } = useNotes()

  // Check if we're on mobile
  const isMobile = typeof window !== 'undefined' && window.innerWidth < 768

  useEffect(() => {
    if (isMobile) {
      setIsSidebarOpen(false)
    }
  }, [isMobile])

  // Auto-select the first note when notes are loaded and none is selected
  useEffect(() => {
    if (notes.length > 0 && selectedNoteId === null) {
      setSelectedNoteId(notes[0].id)
    }
  }, [notes, selectedNoteId])

  const selectedNote = notes.find(note => note.id === selectedNoteId)

  const handleSelectNote = (note: { id: string }) => {
    setSelectedNoteId(note.id)
    if (isMobile) {
      setIsSidebarOpen(false)
    }
  }

  const handleCreateNote = async (data: { title: string; content: string; color: string }) => {
    try {
      const newNote = await createNote(data)
      toast.success('Note created successfully')
      setIsCreateDialogOpen(false)

      // Select the newly created note
      setSelectedNoteId(newNote.id)
    } catch (error) {
      toast.error('Failed to create note')
    }
  }

  const handleUpdateNote = async (id: string, data: Partial<{
    title: string
    content: string
    color: string
  }>) => {
    try {
      await updateNote(id, data)
    } catch (error) {
      toast.error('Failed to update note')
    }
  }

  const handleDeleteNote = async (id: string) => {
    try {
      await deleteNote(id)
      if (selectedNoteId === id) {
        // Select the next available note or null if no notes left
        const remainingNotes = notes.filter(note => note.id !== id)
        setSelectedNoteId(remainingNotes.length > 0 ? remainingNotes[0].id : null)
      }
      toast.success('Note deleted')
    } catch (error) {
      toast.error('Failed to delete note')
    }
  }

  return (
    <div className="flex h-[calc(100vh-4rem)]">
      {/* Sidebar */}
      <div className={cn(
        'flex flex-col border-r bg-gray-50 transition-all duration-300',
        isSidebarOpen ? 'w-80' : 'w-0',
        isMobile && !isSidebarOpen && 'hidden'
      )}>
        <div className="flex items-center justify-between p-4 border-b">
          <h2 className="text-lg font-semibold">Notes</h2>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsCreateDialogOpen(true)}
            className="p-2"
          >
            <Plus className="w-4 h-4" />
          </Button>
        </div>

        <div className="flex-1 overflow-y-auto">
          <NotesList
            notes={notes}
            selectedNoteId={selectedNoteId}
            onSelectNote={handleSelectNote}
            onDeleteNote={handleDeleteNote}
            isLoading={isLoading}
          />
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Mobile Header */}
        {isMobile && (
          <div className="flex items-center justify-between p-4 border-b">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              className="p-2"
            >
              <Menu className="w-4 h-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsCreateDialogOpen(true)}
              className="p-2"
            >
              <Plus className="w-4 h-4" />
            </Button>
          </div>
        )}

        {/* Note Editor */}
        <NoteEditor
          note={selectedNote}
          onUpdate={handleUpdateNote}
          onDelete={handleDeleteNote}
        />
      </div>

      {/* Create Note Dialog */}
      <CreateNoteDialog
        open={isCreateDialogOpen}
        onOpenChange={setIsCreateDialogOpen}
        onCreateNote={handleCreateNote}
      />
    </div>
  )
}

