import { useState } from 'react'
import { format } from 'date-fns'
import { X, Edit3, Trash2, ExternalLink } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog'
import { Badge } from '@/components/ui/badge'
import { cn } from '@/lib/utils'
import { Note } from '@/types/note'
import ReactMarkdown from 'react-markdown'
import remarkGfm from 'remark-gfm'
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter'
import { tomorrow } from 'react-syntax-highlighter/dist/esm/styles/prism'
import { useIsMobile } from '@/hooks/use-mobile'

interface ViewNoteDialogProps {
  note: Note | null
  open: boolean
  onOpenChange: (open: boolean) => void
  onEdit: (note: Note) => void
  onDelete: (id: string) => Promise<void>
}

export function ViewNoteDialog({ note, open, onOpenChange, onEdit, onDelete }: ViewNoteDialogProps) {
  const [isDeleting, setIsDeleting] = useState(false)
  const isMobile = useIsMobile()

  if (!note) return null

  const handleDelete = async () => {
    setIsDeleting(true)
    try {
      await onDelete(note.id)
      onOpenChange(false)
    } finally {
      setIsDeleting(false)
    }
  }

  const handleEdit = () => {
    onOpenChange(false)
    onEdit(note)
  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className={cn(
        "max-w-4xl max-h-[90vh] overflow-hidden",
        isMobile ? "w-full h-full max-w-full max-h-full" : ""
      )}>
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <span className="line-clamp-1">{note.title || 'Untitled Note'}</span>
            <div className="flex items-center gap-2">
              <Badge 
                variant="outline" 
                style={{ 
                  backgroundColor: note.color + '20', 
                  borderColor: note.color,
                  color: note.color 
                }}
              >
                {format(new Date(note.updated_at), 'MMM d, yyyy')}
              </Badge>
            </div>
          </DialogTitle>
        </DialogHeader>

        <div className={cn(
          "overflow-y-auto",
          isMobile ? "h-[calc(100vh-200px)]" : "max-h-[70vh]"
        )}>
          <div className="prose prose-sm dark:prose-invert max-w-none">
            <ReactMarkdown
              remarkPlugins={[remarkGfm]}
              components={{
                code({ node, inline, className, children, ...props }: any) {
                  const match = /language-(\w+)/.exec(className || '')
                  return !inline && match ? (
                    <SyntaxHighlighter
                      style={tomorrow}
                      language={match[1]}
                      PreTag="div"
                      {...props}
                    >
                      {String(children).replace(/\n$/, '')}
                    </SyntaxHighlighter>
                  ) : (
                    <code className={className} {...props}>
                      {children}
                    </code>
                  )
                }
              }}
            >
              {note.content || '*No content yet...*'}
            </ReactMarkdown>
          </div>
        </div>

        <div className="flex justify-between items-center pt-4 border-t">
          <div className="text-sm text-muted-foreground">
            Created {format(new Date(note.created_at), 'MMM d, yyyy')}
            {note.created_at !== note.updated_at && (
              <span> ‚Ä¢ Updated {format(new Date(note.updated_at), 'MMM d, yyyy')}</span>
            )}
          </div>

          <div className="flex gap-2">
            <Button 
              variant="outline" 
              onClick={handleEdit}
              className="gap-2"
            >
              <Edit3 className="h-4 w-4" />
              Edit
            </Button>
            <Button 
              variant="destructive" 
              onClick={handleDelete}
              disabled={isDeleting}
              className="gap-2"
            >
              <Trash2 className="h-4 w-4" />
              {isDeleting ? 'Deleting...' : 'Delete'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  )
}
</action>

