import { useState } from 'react'
import { Search, Filter } from 'lucide-react'
import { Input } from '@/components/ui/input'
import { Button } from '@/components/ui/button'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Skeleton } from '@/components/ui/skeleton'
import { useNotes } from '@/hooks/use-notes'
import { useIsMobile } from '@/hooks/use-mobile'
import { NoteCard } from './NoteCard'
import EmptyState from './EmptyState'
import { cn } from '@/lib/utils'
import { Note } from '@/types/note'

interface NotesListProps {
  onViewNote: (note: Note) => void
}

export function NotesList({ onViewNote }: NotesListProps) {
  const { notes, isLoading, deleteNote, updateNote, createNote } = useNotes()
  const [searchTerm, setSearchTerm] = useState('')
  const [sortBy, setSortBy] = useState<'updated' | 'created' | 'title'>('updated')
  const [filterColor, setFilterColor] = useState<string>('all')
  const isMobile = useIsMobile()

  const filteredNotes = notes
    .filter(note => 
      note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      note.content.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .filter(note => filterColor === 'all' || note.color === filterColor)
    .sort((a, b) => {
      switch (sortBy) {
        case 'updated':
          return new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime()
        case 'created':
          return new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
        case 'title':
          return a.title.localeCompare(b.title)
        default:
          return 0
      }
    })

  if (isLoading) {
    return (
      <div className={cn("space-y-4", isMobile ? "p-2" : "p-4")}>
        {[...Array(3)].map((_, i) => (
          <div key={i} className="space-y-2">
            <Skeleton className="h-4 w-full" />
            <Skeleton className="h-4 w-3/4" />
            <Skeleton className="h-4 w-1/2" />
          </div>
        ))}
      </div>
    )
  }

  if (filteredNotes.length === 0) {
    return <EmptyState onCreateNote={() => createNote({ title: 'New Note', content: '', color: '#ffffff' })} />
  }

  return (
    <div className={cn("space-y-4", isMobile ? "p-2" : "p-4")}>
      <div className={cn(
        "flex gap-2",
        isMobile ? "flex-col" : "flex-row items-center"
      )}>
        <div className="relative flex-1">
          <Search className={cn(
            "absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground",
            isMobile ? "h-4 w-4" : "h-5 w-5"
          )} />
          <Input
            placeholder="Search notes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className={cn(
              "pl-10",
              isMobile ? "text-sm" : "text-base"
            )}
          />
        </div>
        <div className={cn("flex gap-2", isMobile ? "w-full" : "")}>
          <Select value={sortBy} onValueChange={(value: any) => setSortBy(value)}>
            <SelectTrigger className={cn(isMobile ? "w-full" : "w-[120px]")}>
              <SelectValue placeholder="Sort by" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="updated">Updated</SelectItem>
              <SelectItem value="created">Created</SelectItem>
              <SelectItem value="title">Title</SelectItem>
            </SelectContent>
          </Select>
          <Select value={filterColor} onValueChange={setFilterColor}>
            <SelectTrigger className={cn(isMobile ? "w-full" : "w-[120px]")}>
              <SelectValue placeholder="Filter" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Colors</SelectItem>
              <SelectItem value="#ef4444">Red</SelectItem>
              <SelectItem value="#f97316">Orange</SelectItem>
              <SelectItem value="#eab308">Yellow</SelectItem>
              <SelectItem value="#22c55e">Green</SelectItem>
              <SelectItem value="#3b82f6">Blue</SelectItem>
              <SelectItem value="#8b5cf6">Purple</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {filteredNotes.map(note => (
          <NoteCard
            key={note.id}
            note={note}
            onDelete={deleteNote}
            onUpdate={updateNote}
            onView={onViewNote}
          />
        ))}
      </div>
    </div>
  )
}
