import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import NoteForm from './NoteForm';
import { Note, UpdateNoteData } from '@/types/note';

interface EditNoteDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  note: Note | null;
  onUpdate: (id: string, data: UpdateNoteData) => Promise<Note>;
}

export default function EditNoteDialog({ open, onOpenChange, note, onUpdate }: EditNoteDialogProps) {
  const [loading, setLoading] = useState(false);

  const handleUpdate = async (data: UpdateNoteData) => {
    if (!note) return;

    setLoading(true);
    try {
      await onUpdate(note.id, data);
      onOpenChange(false);
    } finally {
      setLoading(false);
    }
  };

  if (!note) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Edit Note</DialogTitle>
        </DialogHeader>
        <NoteForm
          note={note}
          onSubmit={handleUpdate}
          onCancel={() => onOpenChange(false)}
          loading={loading}
        />
      </DialogContent>
    </Dialog>
  );
}

